// src/pages/BooksPage.js
import React, { useEffect, useMemo, useState } from "react";
import { useLocation, useNavigate, Link } from "react-router-dom";

import SiteHeader from "../components/SiteHeader";
import Footer from "../components/Footer";
import BookCard from "../components/BookCard";

import {
  api,
  withKeys,
  fetchJsonWithProxies,
  openPDFOf,
} from "../lib/omekaClient";

/* ===================== Omeka server-side search helpers ===================== */

const RAW_BASE = process.env.REACT_APP_API_BASE_URL || "";
const BASE_URL = RAW_BASE.replace(/\/+$/, "");

/** สร้าง URL /items พร้อมตัวกรองฝั่งเซิร์ฟเวอร์ */
function buildItemsUrl({ q = "", page = 1, perPage = 12, sortBy = "created", sortOrder = "desc" }) {
  const u = new URL(`${BASE_URL}/items`);
  if (q) u.searchParams.set("fulltext_search", q);
  u.searchParams.set("page", String(page));
  u.searchParams.set("per_page", String(perPage));
  u.searchParams.set("sort_by", sortBy);
  u.searchParams.set("sort_order", sortOrder);
  return withKeys(u.toString());
}

/** ยิงตรงเพื่ออ่าน headers (total) ถ้าไม่ได้ค่อย fallback ผ่าน proxy */
async function fetchItemsPage({ q, page, perPage, sortBy, sortOrder }) {
  const url = buildItemsUrl({ q, page, perPage, sortBy, sortOrder });
  try {
    const res = await fetch(url, { method: "GET" });
    const data = await res.json();

    // พยายามอ่าน total จากหลายชื่อ header ที่ Omeka S อาจส่ง
    const h = Object.fromEntries(res.headers.entries());
    const total =
      Number(h["omeka-total-results"]) ||
      Number(h["total-results"]) ||
      Number(h["x-total-results"]) ||
      (h["content-range"] ? Number((h["content-range"].split("/")[1] || "").trim()) : NaN);

    return { data: Array.isArray(data) ? data : [], total: Number.isFinite(total) ? total : null };
  } catch {
    // fallback ผ่าน proxy (เร็ว/ข้าม CORS) -> จะไม่ได้ total
    const data = await fetchJsonWithProxies(url);
    return { data: Array.isArray(data) ? data : [], total: null };
  }
}

/* =============================== BooksPage =============================== */

export default function BooksPage() {
  const location = useLocation();
  const navigate = useNavigate();

  // อ่าน q จาก URL
  const urlQ = useMemo(() => new URLSearchParams(location.search).get("q") || "", [location.search]);

  const [query, setQuery] = useState(urlQ);
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState("");

  // server-side pagination + sort
  const [page, setPage] = useState(1);
  const [perPage, setPerPage] = useState(12);
  const [total, setTotal] = useState(null);
  const [sortBy, setSortBy] = useState("created"); // "created" | "modified" | "id" | "dcterms:title"
  const [sortOrder, setSortOrder] = useState("desc"); // "asc" | "desc"

  // sync query state เมื่อ URL เปลี่ยน (เช่น user กด back/forward)
  useEffect(() => {
    setQuery(urlQ);
    setPage(1);
  }, [urlQ]);

  useEffect(() => {
    (async () => {
      try {
        setLoading(true);
        const { data, total } = await fetchItemsPage({
          q: urlQ.trim(),
          page,
          perPage,
          sortBy,
          sortOrder,
        });
        setItems(data);
        setTotal(total); // อาจเป็น null ถ้าอ่าน header ไม่ได้
        setErr("");
      } catch (e) {
        setErr(e?.message || "โหลดข้อมูลไม่สำเร็จ");
      } finally {
        setLoading(false);
      }
    })();
  }, [urlQ, page, perPage, sortBy, sortOrder]);

  const onSubmitSearch = (e) => {
    e?.preventDefault?.();
    // อัปเดต URL (จะกระตุ้น useEffect โหลดหน้า 1 ให้อัตโนมัติ)
    const qs = new URLSearchParams(location.search);
    if (query.trim()) qs.set("q", query.trim());
    else qs.delete("q");
    navigate(`/books?${qs.toString()}`);
    setPage(1);
  };

  const totalPages = total ? Math.max(1, Math.ceil(total / perPage)) : null;
  const canPrev = page > 1;
  const canNext = totalPages ? page < totalPages : items.length === perPage; // ถ้าไม่รู้ total ให้เดาว่ายังไปต่อได้ถ้าเต็มหน้า

  return (
    <div className="min-h-screen bg-[#fbf6ed] text-[#5b4a3e]">
      <SiteHeader />
      {/* spacer สำหรับ header fixed */}
      <div className="h-[88px]" />

      {/* HERO + ช่องค้นหา */}
      <section className="relative w-full h-[320px] md:h-[380px] overflow-hidden">
        <img
          src="/assets/hero-books.jpg" // เปลี่ยนเป็นภาพของคุณ
          alt=""
          className="absolute inset-0 w-full h-full object-cover"
        />
        <div className="absolute inset-0 bg-black/25" />
        <div className="relative z-10 h-full flex items-center justify-center">
          <form className="flex w-[90%] max-w-2xl" onSubmit={onSubmitSearch}>
            <input
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              className="flex-1 h-12 md:h-14 px-4 rounded-l-full bg-white/95 focus:outline-none"
              placeholder="ค้นหาเอกสารตัวเขียน…"
            />
            <button
              type="submit"
              className="px-5 md:px-6 rounded-r-full bg-[#f08a24] text-white font-bold"
            >
              ค้นหา
            </button>
          </form>
        </div>
      </section>

      {/* breadcrumb */}
      <div className="max-w-7xl mx-auto px-4 sm:px-8 py-4 text-sm text-[#a36a46]">
        <Link to="/" className="hover:underline">หน้าหลัก</Link>
        <span className="mx-2">/</span>
        <span>เอกสารตัวเขียน</span>
      </div>

      {/* หัวข้อ + แถบควบคุม */}
      <div className="max-w-7xl mx-auto px-4 sm:px-8">
        <h1 className="text-2xl md:text-3xl text-center font-semibold">เอกสารตัวเขียน</h1>
        <div className="mt-2 text-center text-sm text-[#8e6f5a]">
          {total != null ? `พบผลลัพธ์ทั้งหมด ${total.toLocaleString("th-TH")} รายการ` : "ค้นหาแบบแบ่งหน้า"}
        </div>

        <div className="mt-4 flex flex-wrap items-center justify-between gap-3">
          <div className="text-sm text-[#8e6f5a]">หน้า {page}{totalPages ? ` / ${totalPages}` : ""}</div>

          <div className="flex items-center gap-3">
            <select
              value={perPage}
              onChange={(e) => { setPerPage(Number(e.target.value)); setPage(1); }}
              className="border border-[#f1e9de] bg-white px-2 py-1 rounded text-sm"
            >
              {[9,12,18,24,36].map(n => <option key={n} value={n}>{n}/หน้า</option>)}
            </select>
            <select
              value={`${sortBy}|${sortOrder}`}
              onChange={(e) => {
                const [sb, so] = e.target.value.split("|");
                setSortBy(sb); setSortOrder(so); setPage(1);
              }}
              className="border border-[#f1e9de] bg-white px-2 py-1 rounded text-sm"
            >
              <option value="created|desc">ใหม่ล่าสุด</option>
              <option value="created|asc">เก่าสุด</option>
              <option value="dcterms:title|asc">ชื่อ (ก→ฮ)</option>
              <option value="dcterms:title|desc">ชื่อ (ฮ→ก)</option>
            </select>
          </div>
        </div>
      </div>

      {/* GRID */}
      <div className="max-w-7xl mx-auto px-4 sm:px-8 mt-6">
        {loading ? (
          <div className="py-20 text-center">กำลังโหลด…</div>
        ) : err ? (
          <div className="py-20 text-center text-red-600">เกิดข้อผิดพลาด: {err}</div>
        ) : items.length === 0 ? (
          <div className="py-20 text-center">ไม่พบรายการ</div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 xl:grid-cols-3 gap-6">
            {items.map((it) => (
              <BookCard key={it["o:id"]} item={it} onOpen={openPDFOf} />
            ))}
          </div>
        )}
      </div>

      {/* Pagination */}
      <div className="max-w-7xl mx-auto px-4 sm:px-8">
        <div className="flex items-center justify-center gap-2 py-8">
          <button
            className="px-3 py-1.5 border border-[#f1e9de] rounded hover:bg-[#fff7ef] disabled:opacity-50"
            onClick={() => setPage((p) => Math.max(1, p - 1))}
            disabled={!canPrev}
          >
            {"<"}
          </button>

          {totalPages ? (
            // windowed numbers  (แสดงได้ 7 เบอร์รอบ ๆ หน้า)
            (() => {
              const win = 7;
              let start = Math.max(1, page - 3);
              let end = Math.min(totalPages, start + win - 1);
              if (end - start + 1 < win) start = Math.max(1, end - win + 1);
              const arr = [];
              for (let n = start; n <= end; n++) arr.push(n);
              return arr.map((n) => (
                <button
                  key={n}
                  className={`px-3 py-1.5 border rounded ${
                    n === page ? "bg-[#d8653b] border-[#d8653b] text-white" : "border-[#f1e9de] hover:bg-[#fff7ef]"
                  }`}
                  onClick={() => setPage(n)}
                >
                  {n}
                </button>
              ));
            })()
          ) : (
            <span className="text-sm text-[#8e6f5a]">กำลังเรียกทีละหน้า…</span>
          )}

          <button
            className="px-3 py-1.5 border border-[#f1e9de] rounded hover:bg-[#fff7ef] disabled:opacity-50"
            onClick={() => setPage((p) => p + 1)}
            disabled={!canNext}
          >
            {">"}
          </button>
        </div>
      </div>

      <Footer />
    </div>
  );
}
