import React, { Suspense } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";

// ✅ เรียกใช้ระบบ i18n หนึ่งครั้งที่ root
import "../i18n";  

// ✅ ครอบแอปด้วย context สำหรับปรับขนาดตัวอักษร
import SettingsProvider from "../context/SettingsContext";

// ✅ import เพจทั้งหมด
import HomePage from "./pages/HomePage";
import BooksPage from "./pages/BooksPage";
import BookArticlesPage from "./pages/BookArticlesPage";
import BookArticleDetail from "./pages/BookArticleDetail";
import ReaderPage from "./pages/ReaderPage";

export default function RootApp() {
  return (
    <SettingsProvider>
      {/* Suspense สำหรับให้ i18next โหลดคำแปลก่อน */}
      <Suspense fallback={<div className="p-6 text-center text-gray-600">Loading…</div>}>
        <BrowserRouter>
          <Routes>
            {/* หน้าแรก */}
            <Route path="/" element={<HomePage />} />

            {/* แคตตาล็อกหนังสือ */}
            <Route path="/books" element={<BooksPage />} />

            {/* หน้าอ่าน PDF แบบใหม่ */}
            <Route path="/read/:id" element={<ReaderPage />} />

            {/* หน้าบทความ */}
            <Route path="/articles" element={<BookArticlesPage />} />
            <Route path="/book/:id" element={<BookArticleDetail />} />
          </Routes>
        </BrowserRouter>
      </Suspense>
    </SettingsProvider>
  );
}
