// src/components/ArticlesBlock.jsx
import React, { useMemo, useRef, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { AnimatePresence, motion } from "framer-motion";
import { useTranslation } from "react-i18next";
import i18n from "../i18n";
import { pickLang } from "../lib/i18nPick";

/** พาเลตต์สีวนแบบเดียวกับ BookArticlesPage */
const BG_PALETTE = [
  { bg: "#ff8a3d", text: "#ffffff" },
  { bg: "#f6d4b1", text: "#5b4a3e" },
  { bg: "#f0c2a8", text: "#5b4a3e" },
  { bg: "#ffd7a0", text: "#5b4a3e" },
  { bg: "#ffe9d6", text: "#5b4a3e" },
];

export default function ArticlesBlock({ title, desc, items = [], loading, err, onOpen }) {
  const { t } = useTranslation();
  const [index, setIndex] = useState(0); // สไลด์มือถือ

  // สั้นกระชับคำอธิบาย
  const cleanText = (html = "", max = 180) => {
    if (!html) return "";
    const tmp = document.createElement("div");
    tmp.innerHTML = html;
    const txt = (tmp.textContent || tmp.innerText || "").trim();
    return txt.length > max ? txt.slice(0, max) + "…" : txt;
  };

  const next = () => setIndex((i) => (items.length ? (i + 1) % Math.min(items.length, 9) : 0));
  const prev = () =>
    setIndex((i) =>
      items.length ? (i - 1 + Math.min(items.length, 9)) % Math.min(items.length, 9) : 0
    );

  const top6 = useMemo(() => items.slice(0, 6), [items]);
  const mobileCurrent = useMemo(() => items.slice(0, 9)[index], [items, index]);

  return (
    <section className="rounded-2xl bg-white/70 backdrop-blur-sm shadow-sm ring-1 ring-black/5">
      <div className="px-5 pt-6 pb-2 text-center">
        <h2 className="text-2xl sm:text-3xl font-bold tracking-tight text-[#5b4a3e]">
          {title}
        </h2>
        {desc && <p className="text-sm sm:text-base text-black/70 mt-2">{desc}</p>}
      </div>

      <div className="px-5 pb-6">
        <AnimatePresence mode="wait">
          {loading ? (
            <motion.div
              key="art-loading"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4"
            >
              {Array.from({ length: 6 }).map((_, i) => (
                <div key={i} className="border rounded-xl p-4 bg-white ring-1 ring-black/5">
                  <div className="h-5 bg-neutral-100 animate-pulse rounded w-2/3" />
                  <div className="mt-3 space-y-2">
                    <div className="h-3 bg-neutral-100 animate-pulse rounded" />
                    <div className="h-3 bg-neutral-100 animate-pulse rounded w-5/6" />
                    <div className="h-3 bg-neutral-100 animate-pulse rounded w-4/6" />
                  </div>
                </div>
              ))}
            </motion.div>
          ) : err ? (
            <motion.p
              key="art-err"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              className="text-center text-sm text-red-600"
            >
              {t("errors.loadFailed")}
            </motion.p>
          ) : items.length === 0 ? (
            <motion.p
              key="art-empty"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              className="text-center text-sm text-black/60"
            >
              {t("common.emptyArticles")}
            </motion.p>
          ) : (
            <motion.div
              key="art-grid"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
            >
              {/* เดสก์ท็อป: การ์ด 3x2 */}
              {/* เดสก์ท็อป: 1 บทความต่อแถว (layout แนวนอน) */}
            <div className="hidden sm:flex flex-col gap-5">
            {top6.map((it, i) => (
                <ArticleRow key={it["o:id"]} item={it} paletteIndex={i} onOpen={onOpen} />
            ))}
            </div>


              {/* มือถือ: สไลด์ทีละ 1 + ลูกศร + จุด */}
              <div className="sm:hidden relative">
                <div className="relative overflow-hidden rounded-xl ring-1 ring-black/5">
                  <div className="aspect-[16/11] w-full">
                    <MobileArticleSlide item={mobileCurrent} />
                  </div>

                  <div className="absolute inset-0 flex items-center justify-between px-2">
                    <button
                      aria-label={t("carousel.prev")}
                      onClick={prev}
                      className="h-9 w-9 rounded-full bg-black/40 hover:bg-black/60 text-white grid place-items-center"
                    >
                      ‹
                    </button>
                    <button
                      aria-label={t("carousel.next")}
                      onClick={next}
                      className="h-9 w-9 rounded-full bg-black/40 hover:bg-black/60 text-white grid place-items-center"
                    >
                      ›
                    </button>
                  </div>
                </div>

                <div className="mt-2 flex items-center justify-center gap-1.5">
                  {items.slice(0, 9).map((_, i) => (
                    <button
                      key={i}
                      onClick={() => setIndex(i)}
                      className={`h-2.5 w-2.5 rounded-full ${
                        i === index ? "bg-[#5b4a3e]" : "bg-black/20"
                      }`}
                      aria-label={t("carousel.goToSlide", { n: i + 1 })}
                      aria-current={i === index ? "true" : "false"}
                    />
                  ))}
                </div>
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </section>
  );
}

/* การ์ดบทความ (เดสก์ท็อป) */
function ArticleCard({ item, paletteIndex = 0, onOpen }) {
  const { t } = useTranslation();
  const lang = (i18n.language || "th").split("-")[0];

  const id = item?.["o:id"];
  const title = (pickLang(item?.["dcterms:title"], lang) || "").trim() || `#${id}`;
  const rawDesc = pickLang(item?.["dcterms:description"], lang) || "";

  // clean text
  const tmp = document.createElement("div");
  tmp.innerHTML = rawDesc;
  const clean = (tmp.textContent || tmp.innerText || "").trim();
  const desc = clean.length > 180 ? clean.slice(0, 180) + "…" : clean;

  const { bg, text } = BG_PALETTE[paletteIndex % BG_PALETTE.length];
  const isLight = text.toLowerCase() === "#ffffff";

  return (
    <article
      className="group relative border rounded-xl p-4 shadow-sm ring-1 ring-black/5 overflow-hidden"
      style={{ background: bg, color: text }}
    >
      <h3 className="font-semibold line-clamp-2">{title}</h3>
      {desc && <p className="mt-2 text-sm/relaxed line-clamp-4 opacity-90">{desc}</p>}

      <div className="mt-4 flex gap-2">
        <button
          type="button"
          onClick={() => onOpen?.(item)}
          className={`h-9 px-4 rounded-full text-sm font-semibold shadow ${
            isLight ? "bg-white text-[#5b4a3e]" : "bg-[#d8653b] text-white"
          }`}
        >
          {t("actions.readNow", "Read now")}
        </button>
        <Link
          to={`/book/${id}`}
          className={`h-9 px-4 rounded-full text-sm grid place-items-center ring-1 ${
            isLight ? "bg-white/10 text-white ring-white/40" : "bg-black/10 text-[#5b4a3e] ring-black/10"
          }`}
        >
          {t("actions.details", "Details")}
        </Link>
      </div>

      {/* ลวดลายวงกลมจาง ๆ ด้านล่าง */}
      <div className="pointer-events-none absolute -right-6 -bottom-6 h-24 w-24 rounded-full opacity-10 bg-black" />
    </article>
  );
}

/* สไลด์บทความ (มือถือ) */
function MobileArticleSlide({ item }) {
  const { t } = useTranslation();
  const navigate = useNavigate();

  if (!item) return <div className="w-full h-full bg-white" />;

  const id = item["o:id"];
  const lang = (i18n.language || "th").split("-")[0];
  const title = (pickLang(item?.["dcterms:title"], lang) || "").trim() || `#${id}`;
  const rawDesc = pickLang(item?.["dcterms:description"], lang) || "";

  const tmp = document.createElement("div");
  tmp.innerHTML = rawDesc;
  const clean = (tmp.textContent || tmp.innerText || "").trim();
  const desc = clean.length > 180 ? clean.slice(0, 180) + "…" : clean;

  const { bg, text } = BG_PALETTE[id % BG_PALETTE.length]; // วนด้วย id
  const isLight = text.toLowerCase() === "#ffffff";

  return (
    <div className="w-full h-full p-4" style={{ background: bg, color: text }}>
      <h3 className="font-semibold line-clamp-2">{title}</h3>
      {desc && <p className="mt-2 text-sm/relaxed line-clamp-5 opacity-90">{desc}</p>}

      <div className="mt-4 flex gap-2">
        <button
          onClick={() => navigate(`/read/${id}`)}
          className={`h-10 px-5 rounded-full text-sm font-semibold shadow ${
            isLight ? "bg-white text-[#5b4a3e]" : "bg-[#d8653b] text-white"
          }`}
        >
          {t("actions.readNow", "Read now")}
        </button>
        <Link
          to={`/book/${id}`}
          className={`h-10 px-5 rounded-full text-sm grid place-items-center ring-1 ${
            isLight ? "bg-white/10 text-white ring-white/40" : "bg-black/10 text-[#5b4a3e] ring-black/10"
          }`}
        >
          {t("actions.details", "Details")}
        </Link>
      </div>
    </div>
  );
}
