// src/pages/BookArticlesPage.jsx
import React, { useEffect, useMemo, useRef, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import SiteHeader from "../components/SiteHeader";
import Footer from "../components/Footer";
import BackToTop from "../components/BackToTop";
import { fetchItemsLite, titleOf, descOf } from "../lib/omekaClient";

/* helpers */
const toHttps = (u) => (u ? (u.startsWith("http://") ? u.replace(/^http:/, "https:") : u) : "");
const stripTags = (html = "") => {
  if (!html) return "";
  const tmp = document.createElement("div");
  tmp.innerHTML = html;
  return (tmp.textContent || tmp.innerText || "").trim();
};

/* สีพื้นหลังสำรองสำหรับแต่ละสไลด์ (ถ้าไม่ได้ดึงสีจากภาพ) */
const BG_PALETTE = ["#ff8a3d", "#f6d4b1", "#f0c2a8", "#ffd7a0", "#ffe9d6"];

export default function BookArticlesPage() {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    (async () => {
      try {
        const res = await fetchItemsLite();
        const filtered = (res || []).filter(
          (it) => Array.isArray(it["dcterms:description"]) && it["dcterms:description"].length > 0
        );
        setBooks(filtered);
      } catch (e) {
        console.error("โหลดรายการล้มเหลว", e);
      } finally {
        setLoading(false);
      }
    })();
  }, []);

  if (loading) {
    return (
      <div className="min-h-screen flex flex-col bg-[#faf7f2]">
        <SiteHeader />
        <div className="flex-1 grid place-items-center text-[#7b6c61]">กำลังโหลด…</div>
        <Footer />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-[#faf7f2] text-[#111518]">
      <SiteHeader />
      <FullscreenHeroCarousel items={books} />
      <Footer />
      <BackToTop />
    </div>
  );
}

/* ================== Fullscreen hero carousel (responsive) ================== */
function FullscreenHeroCarousel({ items = [] }) {
  const trackRef = useRef(null);
  const [index, setIndex] = useState(0);
  const navigate = useNavigate();

  /* ใช้ dynamic viewport + header สูงต่างกันตามขนาดจอ */
  const slideStyle = {
    // header สูง ~72px บนมือถือ, ~96px บน md+
    "--headerH": "72px",
    height: "calc(100dvh - var(--headerH))",
  };
  const slideStyleMd = {
    "--headerH": "96px",
    height: "calc(100dvh - var(--headerH))",
  };

  const onScroll = () => {
    const el = trackRef.current;
    if (!el) return;
    const i = Math.round(el.scrollLeft / el.clientWidth);
    setIndex(i);
  };

  /* scroll แนวนอนด้วยล้อเมาส์; บนมือถือ/ทัช เราปล่อยให้ pan-x ตามธรรมชาติ */
  const onWheel = (e) => {
    const el = trackRef.current;
    if (!el) return;
    if (Math.abs(e.deltaY) > Math.abs(e.deltaX)) {
      e.preventDefault();
      el.scrollLeft += e.deltaY;
    }
  };

  const scrollTo = (i) => {
    const el = trackRef.current;
    if (!el) return;
    const next = Math.max(0, Math.min(items.length - 1, i));
    el.scrollTo({ left: next * el.clientWidth, behavior: "smooth" });
    setIndex(next);
  };

  useEffect(() => {
    const h = (e) => {
      if (e.key === "ArrowRight") scrollTo(index + 1);
      if (e.key === "ArrowLeft") scrollTo(index - 1);
    };
    window.addEventListener("keydown", h);
    return () => window.removeEventListener("keydown", h);
  }, [index]);

  const slides = useMemo(
    () =>
      items.map((book, i) => {
        const id = book["o:id"];
        const title = titleOf(book) || `Item #${id}`;
        const rawDesc = descOf(book) || "";
        const cleanDesc = stripTags(rawDesc);
        const desc =
          (cleanDesc || " ").slice(0, 220) + (cleanDesc && cleanDesc.length > 220 ? "…" : "");
        const rawThumb =
          book?.thumbnail_display_urls?.large ||
          book?.thumbnail_display_urls?.medium ||
          book?.["o:thumbnail_urls"]?.large ||
          book?.["o:thumbnail_urls"]?.medium ||
          "";
        const cover = toHttps(rawThumb) || "/assets/placeholder.webp";
        const base = BG_PALETTE[i % BG_PALETTE.length];

        return (
          <section
            key={id}
            className="snap-start shrink-0 w-full relative overflow-hidden"
            style={slideStyle}
          >
            {/* md+: ใช้ความสูงอีกรอบเพื่อกัน Safari glitch */}
            <div className="hidden md:block absolute inset-0" style={slideStyleMd} />

            {/* พื้นหลัง gradient ช่วยคอนทราสต์ (ส้ม → ส้มเข้ม) */}
            <div
              className="absolute inset-0 -z-10"
              style={{
                background: `linear-gradient(135deg, ${base} 0%, rgba(0,0,0,0.05) 100%)`,
              }}
            />

            {/* overlay ด้านล่างให้ดูมีมิติ */}
            <div className="absolute inset-x-0 bottom-0 h-40 bg-black/10 blur-3xl pointer-events-none" />

            {/* คอนเทนต์: มือถือ = เรียงตั้ง, เดสก์ท็อป = 2 คอลัมน์ */}
            <div
              className="h-full max-w-[1200px] mx-auto px-5 sm:px-6 lg:px-8 
                         grid grid-rows-[auto_1fr] md:grid-rows-none 
                         grid-cols-1 md:grid-cols-2 gap-4 md:gap-8 items-center"
            >
              {/* รูปปก (อยู่บนในมือถือ, ขวาในเดสก์ท็อป) */}
              <div className="order-1 md:order-2 relative pt-4 md:pt-0">
                <div className="relative mx-auto w-[86%] sm:w-[72%] md:w-[85%] lg:w-[78%] aspect-[3/4]">
                  <img
                    src={cover}
                    alt={title}
                    className="absolute inset-0 w-full h-full object-contain drop-shadow-2xl"
                    loading="lazy"
                    decoding="async"
                  />
                  {/* เงาวางบนพื้น */}
                  <div className="absolute -bottom-5 inset-x-0 mx-auto h-9 w-4/5 rounded-full bg-black/25 blur-xl opacity-40" />
                </div>
              </div>

              {/* ข้อความ + ปุ่ม (อยู่ล่างในมือถือ, ซ้ายในเดสก์ท็อป) */}
              <div className="order-2 md:order-1 text-white pb-[env(safe-area-inset-bottom)]">
                <h2
                  className="font-extrabold leading-[0.95]"
                  style={{ fontSize: "clamp(1.9rem, 4vw + 1rem, 5.5rem)" }}
                >
                  {title}
                </h2>

                <p
                  className="mt-3 sm:mt-4 text-white/95 max-w-xl"
                  style={{ fontSize: "clamp(0.95rem, 0.7vw + 0.85rem, 1.15rem)" }}
                >
                  {desc}
                </p>

                <div className="mt-5 sm:mt-7 flex flex-wrap gap-3">
                  <button
                    onClick={() => navigate(`/book/${id}`)}
                    className="inline-flex items-center h-11 sm:h-12 px-5 sm:px-6 rounded-full bg-white text-[#5b4a3e] shadow
                               hover:opacity-95 transition font-semibold"
                  >
                    Discover <span className="ml-2">→</span>
                  </button>
                  <Link
                    to={`/read/${id}`}
                    className="inline-flex items-center h-11 sm:h-12 px-5 sm:px-6 rounded-full bg-white/20 text-white ring-1 ring-white/40
                               hover:bg-white/25 transition"
                  >
                    Read now
                  </Link>
                </div>
              </div>
            </div>
          </section>
        );
      }),
    [items]
  );

  if (items.length === 0) {
    return (
      <main className="pt-28 pb-16">
        <p className="text-center text-[#7b6c61]">ยังไม่มีรายการสำหรับแสดง</p>
      </main>
    );
  }

  return (
    <div className="relative">
      {/* แทร็กสไลด์แนวนอนเต็มจอ */}
      <div
        ref={trackRef}
        onScroll={onScroll}
        onWheel={onWheel}
        className="snap-x snap-mandatory overflow-x-auto overflow-y-hidden w-full
                   [scrollbar-width:none] [-ms-overflow-style:none] [&::-webkit-scrollbar]:hidden
                   touch-pan-x"  /* tailwind 3.4: sets touch-action: pan-x */
        style={slideStyle}
      >
        <div className="flex w-max" style={{ height: "100%", minWidth: "100%" }}>
          {slides}
        </div>
      </div>

      {/* ลูกศรซ้าย/ขวา (ซ่อนบนจอเล็ก, คลิกได้บนจอใหญ่) */}
      <div className="pointer-events-none absolute inset-y-0 left-2 right-2 flex items-center justify-between">
        <button
          className="pointer-events-auto hidden md:grid place-items-center h-11 w-11 rounded-full bg-white/90 shadow
                     ring-1 ring-black/10 hover:bg-white transition"
          onClick={() => scrollTo(index - 1)}
          aria-label="Prev"
        >
          ‹
        </button>
        <button
          className="pointer-events-auto hidden md:grid place-items-center h-11 w-11 rounded-full bg-white/90 shadow
                     ring-1 ring-black/10 hover:bg-white transition"
          onClick={() => scrollTo(index + 1)}
          aria-label="Next"
        >
          ›
        </button>
      </div>

      {/* ดอทบอกตำแหน่งสไลด์ (รองรับ safe-area) */}
      <div className="absolute left-1/2 -translate-x-1/2 bottom-[calc(1rem+env(safe-area-inset-bottom))] flex gap-2">
        {items.map((_, i) => (
          <button
            key={i}
            onClick={() => scrollTo(i)}
            className={`h-2.5 rounded-full transition-all ${
              index === i ? "w-8 bg-white" : "w-2.5 bg-white/60 hover:bg-white"
            }`}
            aria-label={`Go to slide ${i + 1}`}
          />
        ))}
      </div>
    </div>
  );
}
