import React, { useCallback, useEffect, useMemo, useRef, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { Document, Page, pdfjs } from "react-pdf";
import "react-pdf/dist/Page/TextLayer.css";
import "react-pdf/dist/Page/AnnotationLayer.css";
import axios from "axios";

/** ====== ตั้งค่า pdf.js worker ====== */
// วิธีที่เข้ากันได้กว้างสุด (ใช้ CDN ตาม version ของ pdfjs)
// หมายเหตุ: ไม่บังคับให้ผู้ใช้ดาวน์โหลดไฟล์ แค่ใช้ worker โหลดเอกสาร
pdfjs.GlobalWorkerOptions.workerSrc = 
  `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.min.js`;

/** ====== ENV & URL helpers (เหมือนไฟล์อื่นของคุณ) ====== */
const RAW_BASE = process.env.REACT_APP_API_BASE_URL || "";
const BASE_URL = RAW_BASE.replace(/\/+$/, "");
const withKeys = (url) => {
  const key_identity = process.env.REACT_APP_API_KEY_IDENTITY;
  const key_credential = process.env.REACT_APP_API_KEY_CREDENTIAL;
  const sep = url.includes("?") ? "&" : "?";
  return `${url}${sep}key_identity=${key_identity}&key_credential=${key_credential}`;
};
const api = (path) =>
  withKeys(`${BASE_URL}${path.startsWith("/") ? path : `/${path}`}`);

const fetchJsonWithProxies = async (finalUrl) => {
  try {
    const viaAllorigins = `https://api.allorigins.win/get?url=${encodeURIComponent(finalUrl)}`;
    const r1 = await axios.get(viaAllorigins, { timeout: 15000 });
    return JSON.parse(r1.data.contents);
  } catch {}
  try {
    const viaCodetabs = `https://api.codetabs.com/v1/proxy?quest=${encodeURIComponent(finalUrl)}`;
    const r2 = await axios.get(viaCodetabs, { timeout: 15000 });
    return r2.data;
  } catch {}
  const r3 = await axios.get(finalUrl, { timeout: 15000 });
  return r3.data;
};

export default function PdfReaderPage() {
  const { mediaId } = useParams();               // /read/:mediaId
  const navigate = useNavigate();

  const [pdfUrl, setPdfUrl] = useState(null);
  const [numPages, setNumPages] = useState(null);
  const [page, setPage] = useState(1);
  const [scale, setScale] = useState(1.1);
  const [loadingDoc, setLoadingDoc] = useState(true);
  const [spread, setSpread] = useState(null);    // null=auto, true=two-page, false=single

  // auto spread: >= 1024px = two-page, else single
  useEffect(() => {
    const update = () => {
      if (spread === null) {
        setSpread(window.innerWidth >= 1024 ? true : false);
      }
    };
    update();
    window.addEventListener("resize", update);
    return () => window.removeEventListener("resize", update);
  }, [spread]);

  // โหลด URL ของไฟล์จริงจาก Omeka media
  useEffect(() => {
    (async () => {
      try {
        setLoadingDoc(true);
        const media = await fetchJsonWithProxies(api(`/media/${mediaId}`));
        const url = media?.["o:original_url"];
        if (!url) throw new Error("ไม่พบ URL ของไฟล์ PDF");
        setPdfUrl(url);
      } catch (e) {
        alert(e?.message || "โหลดไฟล์ไม่สำเร็จ");
        navigate(-1);
      } finally {
        setLoadingDoc(false);
      }
    })();
  }, [mediaId, navigate]);

  const onLoadSuccess = useCallback((info) => {
    setNumPages(info.numPages || 0);
    setPage(1);
  }, []);

  const canPrev = page > 1;
  const canNext = useMemo(() => {
    if (!numPages) return false;
    return spread ? page + 1 <= numPages : page < numPages;
  }, [page, numPages, spread]);

  const prevPage = () => setPage((p) => Math.max(1, spread ? p - 2 : p - 1));
  const nextPage = () =>
    setPage((p) => {
      if (!numPages) return p;
      return Math.min(numPages, spread ? p + 2 : p + 1);
    });

  // ป้องกันคลิกขวา + drag เล็กน้อย (กันสำเนาง่าย ๆ — กันได้ระดับหนึ่งเท่านั้น)
  const blockerRef = useRef(null);
  useEffect(() => {
    const el = blockerRef.current;
    if (!el) return;
    const onContext = (e) => e.preventDefault();
    el.addEventListener("contextmenu", onContext);
    return () => el.removeEventListener("contextmenu", onContext);
  }, []);

  return (
    <div className="min-h-screen bg-[#f6efe5] text-[#5b4a3e]">
      {/* แถบควบคุมด้านบน */}
      <div className="sticky top-0 z-40 bg-white/90 backdrop-blur border-b border-[#eaded0]">
        <div className="max-w-7xl mx-auto flex items-center justify-between px-4 sm:px-6 py-3 gap-3">
          <div className="flex items-center gap-2">
            <button
              onClick={() => navigate(-1)}
              className="px-3 py-1.5 rounded border border-[#eaded0] hover:bg-[#fff7ef]"
              title="ย้อนกลับ"
            >
              ← กลับ
            </button>
            <div className="text-sm opacity-70">
              {numPages ? `หน้า ${page}${spread ? `–${Math.min(numPages, page + 1)}` : ""} / ${numPages}` : "กำลังเตรียมเอกสาร…"}
            </div>
          </div>

          <div className="flex items-center gap-2">
            <button
              onClick={() => setScale((s) => Math.max(0.6, s - 0.1))}
              className="px-3 py-1.5 rounded border border-[#eaded0] hover:bg-[#fff7ef]"
              title="ซูมออก"
            >
              −
            </button>
            <span className="text-sm w-12 text-center">{Math.round(scale * 100)}%</span>
            <button
              onClick={() => setScale((s) => Math.min(3, s + 0.1))}
              className="px-3 py-1.5 rounded border border-[#eaded0] hover:bg-[#fff7ef]"
              title="ซูมเข้า"
            >
              +
            </button>

            <div className="w-px h-6 bg-[#eaded0] mx-1" />

            <button
              onClick={() => setSpread((v) => (v === null ? true : !v))}
              className="px-3 py-1.5 rounded border border-[#eaded0] hover:bg-[#fff7ef]"
              title="สลับโหมด หน้าคู่/หน้าเดี่ยว"
            >
              {spread ? "หน้าคู่" : "หน้าเดี่ยว"}
            </button>
          </div>

          <div className="flex items-center gap-2">
            {/* ไอคอน “กำลังโหลด” แทนสัญลักษณ์ดาวน์โหลด */}
            {loadingDoc && (
              <span className="animate-pulse text-sm flex items-center gap-1">
                <span className="material-symbols-outlined">download</span>
                กำลังโหลด…
              </span>
            )}
          </div>
        </div>
      </div>

      {/* ส่วนตัวเอกสาร */}
      <div
        ref={blockerRef}
        className="max-w-7xl mx-auto px-3 sm:px-6 py-4 select-none"
      >
        {!pdfUrl ? (
          <div className="text-center py-20">กำลังโหลดเอกสาร…</div>
        ) : (
          <>
            <div className="flex items-center justify-center gap-2 mb-3">
              <button
                onClick={prevPage}
                disabled={!canPrev}
                className="px-3 py-1.5 rounded border border-[#eaded0] hover:bg-[#fff7ef] disabled:opacity-40"
              >
                ก่อนหน้า
              </button>
              <button
                onClick={nextPage}
                disabled={!canNext}
                className="px-3 py-1.5 rounded border border-[#eaded0] hover:bg-[#fff7ef] disabled:opacity-40"
              >
                ถัดไป
              </button>
            </div>

            <div className="flex items-start justify-center gap-4">
              <Document
                file={pdfUrl}
                onLoadSuccess={onLoadSuccess}
                loading={
                  <div className="text-center py-10 opacity-70">
                    กำลังเรนเดอร์หน้า…
                  </div>
                }
                renderMode="canvas"
                options={{ standardFontDataUrl: "" }}
              >
                {/* โหมดหน้าเดี่ยว */}
                {!spread && <Page pageNumber={page} scale={scale} renderTextLayer renderAnnotationLayer />}

                {/* โหมดหน้าคู่: แสดงหน้าปัจจุบันและหน้าถัดไป */}
                {spread && (
                  <div className="flex gap-4">
                    <Page pageNumber={page} scale={scale} renderTextLayer renderAnnotationLayer />
                    {page + 1 <= numPages && (
                      <Page pageNumber={page + 1} scale={scale} renderTextLayer renderAnnotationLayer />
                    )}
                  </div>
                )}
              </Document>
            </div>
          </>
        )}
      </div>
    </div>
  );
}
