export default function BooksPage() {
  const location = useLocation();
  const navigate = useNavigate();
  const [query, setQuery] = useState(new URLSearchParams(location.search).get("q") || "");
  const [items, setItems] = useState([]);
  const [filters, setFilters] = useState({
    subjects: [],
    types: [],
    languages: [],
    sources: [],
  });
  const [selected, setSelected] = useState({
    subject: null,
    type: null,
    language: null,
    source: null,
  });
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState("");
  const [page, setPage] = useState(1);
  const [total, setTotal] = useState(0);
  const [grid, setGrid] = useState(true);

// อยู่ภายใน BooksPage component ของคุณ
const buildUrl = () => {
  // ถ้าใช้ api("/items") อยู่แล้ว จะมี key ติดให้ด้วย ไม่ต้อง withKeys ซ้ำ
  const base = new URL(api("/items"));

  if (query) base.searchParams.set("fulltext_search", query.trim());
  base.searchParams.set("page", String(page));
  base.searchParams.set("per_page", "12");

  // เติมตัวกรองแบบ Omeka S property[...] อย่างถูกต้อง (ใส่วงเล็บปีกกา)
  let i = 0;

  if (selected.subject) {
    base.searchParams.set(`property[${i}][property]`, "dcterms:subject");
    base.searchParams.set(`property[${i}][type]`, "eq");
    base.searchParams.set(`property[${i}][text]`, selected.subject);
    i++;
  }

  if (selected.type) {
    base.searchParams.set(`property[${i}][property]`, "dcterms:type");
    base.searchParams.set(`property[${i}][type]`, "eq");
    base.searchParams.set(`property[${i}][text]`, selected.type);
    i++;
  }

  if (selected.language) {
    base.searchParams.set(`property[${i}][property]`, "dcterms:language");
    base.searchParams.set(`property[${i}][type]`, "eq");
    base.searchParams.set(`property[${i}][text]`, selected.language);
    i++;
  }

  if (selected.source) {
    base.searchParams.set(`property[${i}][property]`, "dcterms:source");
    base.searchParams.set(`property[${i}][type]`, "eq");
    base.searchParams.set(`property[${i}][text]`, selected.source);
    i++;
  }

  return base.toString();
};


  /* โหลดข้อมูลรายการ */
  useEffect(() => {
    const load = async () => {
      try {
        setLoading(true);
        const url = buildUrl();
        const r = await fetchJsonWithProxies(url);
        setItems(Array.isArray(r) ? r : []);
        setTotal(r.length);
      } catch (e) {
        setErr(e.message);
      } finally {
        setLoading(false);
      }
    };
    load();
  }, [query, page, selected]);

  /* โหลด facet filters */
  useEffect(() => {
    const loadFilters = async () => {
      const fetchFacet = async (field) => {
        const url = api(`/items?facet[0][field]=${field}&facet[0][type]=terms&facet[0][limit]=20`);
        const res = await fetchJsonWithProxies(url);
        // สมมุติว่าคุณมี endpoint facet summary (หรือสามารถใช้ custom API)
        return (res.facets && res.facets[field]) || [];
      };
      setFilters({
        subjects: await fetchFacet("dcterms:subject"),
        types: await fetchFacet("dcterms:type"),
        languages: await fetchFacet("dcterms:language"),
        sources: await fetchFacet("dcterms:source"),
      });
    };
    loadFilters();
  }, []);

  const openPDF = async (item) => {
    try {
      await openPDFOf(item);
    } catch (e) {
      alert(e.message);
    }
  };

  const handleFilterChange = (key, value) => {
    setSelected((prev) => ({ ...prev, [key]: value }));
    setPage(1);
  };

  return (
    <div className="min-h-screen bg-[#fbf6ed] text-[#5b4a3e]">
      <SiteHeader />
      <div className="h-[88px]" />

      {/* HERO */}
      <section className="relative w-full h-[300px] md:h-[360px] overflow-hidden">
        <img src="/assets/hero-books.jpg" alt="" className="absolute inset-0 w-full h-full object-cover" />
        <div className="absolute inset-0 bg-black/25" />
        <div className="relative z-10 h-full flex items-center justify-center">
          <form
            onSubmit={(e) => {
              e.preventDefault();
              navigate(`/books?q=${encodeURIComponent(query)}`);
            }}
            className="flex w-[90%] max-w-2xl"
          >
            <input
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              className="flex-1 h-12 md:h-14 px-4 rounded-l-full bg-white/90 focus:outline-none"
              placeholder="ค้นหาเอกสารตัวเขียน..."
            />
            <button type="submit" className="px-5 rounded-r-full bg-[#f08a24] text-white font-bold">
              ค้นหา
            </button>
          </form>
        </div>
      </section>

      {/* Breadcrumb */}
      <div className="max-w-7xl mx-auto px-4 sm:px-8 py-4 text-sm text-[#a36a46]">
        <Link to="/" className="hover:underline">หน้าหลัก</Link>
        <span className="mx-2">/</span>
        <span>เอกสารตัวเขียน</span>
      </div>

      <div className="max-w-7xl mx-auto flex flex-col md:flex-row gap-6 px-4 sm:px-8">
        {/* Sidebar */}
        <FilterSidebar filters={filters} selected={selected} onChange={handleFilterChange} />

        {/* Main content */}
        <div className="flex-1">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-xl font-semibold">เอกสารตัวเขียน</h2>
            <div className="flex gap-2">
              <button
                onClick={() => setGrid(true)}
                className={`p-2 border rounded ${grid ? "bg-[#f3d1b3]" : ""}`}
              >
                ☷
              </button>
              <button
                onClick={() => setGrid(false)}
                className={`p-2 border rounded ${!grid ? "bg-[#f3d1b3]" : ""}`}
              >
                ≣
              </button>
            </div>
          </div>

          {loading ? (
            <div className="text-center py-20">กำลังโหลด...</div>
          ) : err ? (
            <div className="text-center text-red-600">{err}</div>
          ) : (
            <div
              className={
                grid
                  ? "grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6"
                  : "flex flex-col gap-3"
              }
            >
              {items.map((item) => (
                <BookCard key={item["o:id"]} item={item} onOpen={openPDF} />
              ))}
            </div>
          )}
        </div>
      </div>

      <Footer />
    </div>
  );
}
