import React, { useEffect, useMemo, useState } from "react";
import { useParams, Link } from "react-router-dom";
import SiteHeader from "../components/SiteHeader";
import Footer from "../components/Footer";
import BackToTop from "../components/BackToTop";
import { api, fetchJsonWithProxies, titleOf } from "../lib/omekaClient";

/** helpers */
const getList = (item, key) => (Array.isArray(item?.[key]) ? item[key] : []);
const getVal = (item, key, fallback = "-") => item?.[key]?.[0]?.["@value"] ?? fallback;
const normalizeHttps = (raw) => (raw ? (raw.startsWith("http://") ? raw.replace(/^http:/, "https:") : raw) : "");

// ตรวจจับมือถือ (UA + viewport)
const useIsMobile = () => {
  const [isMobile, setIsMobile] = useState(false);
  useEffect(() => {
    const uaIsMobile = /Android|iPhone|iPad|iPod/i.test(navigator.userAgent);
    const mq = window.matchMedia("(max-width: 768px)");
    const update = () => setIsMobile(uaIsMobile || mq.matches);
    update();
    mq.addEventListener("change", update);
    return () => mq.removeEventListener("change", update);
  }, []);
  return isMobile;
};

export default function ParallaxBookDetail() {
  const { id } = useParams();
  const [item, setItem] = useState(null);
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState("");
  const isMobile = useIsMobile();

  useEffect(() => {
    let alive = true;
    (async () => {
      try {
        setLoading(true);
        setErr("");
        const data = await fetchJsonWithProxies(api(`/items/${id}`));
        if (!alive) return;
        setItem(data);
        const t = titleOf(data) || data?.["o:title"] || "รายละเอียดหนังสือ";
        document.title = `${t} · Parallax Detail`;
      } catch (e) {
        if (!alive) return;
        setErr(e?.message || "โหลดข้อมูลไม่สำเร็จ");
      } finally {
        if (alive) setLoading(false);
      }
    })();
    return () => {
      alive = false;
    };
  }, [id]);

  const meta = useMemo(() => {
    if (!item) return {};
    return {
      title: titleOf(item) || item?.["o:title"],
      creator: getVal(item, "dcterms:creator"),
      date: getVal(item, "dcterms:date"),
      extent: getVal(item, "dcterms:extent", ""),
      identifier: getVal(item, "dcterms:identifier", ""),
      type: getVal(item, "dcterms:type", ""),
      format: getVal(item, "dcterms:format", ""),
      publisher: getVal(item, "dcterms:publisher", ""),
      language: getVal(item, "dcterms:language", ""),
      rights: getVal(item, "dcterms:rights", ""),
      thumb:
        item?.thumbnail_display_urls?.large ||
        item?.thumbnail_display_urls?.medium ||
        "/assets/placeholder.webp",
    };
  }, [item]);

  const descriptions = useMemo(() => {
    const arr = getList(item, "dcterms:description");
    const vals = arr.map((d) => (typeof d?.["@value"] === "string" ? d["@value"].trim() : "")).filter(Boolean);
    return vals;
  }, [item]);

  // ถ้า media แรกเป็น PDF เราไม่ใช้ในหน้านี้ แต่เก็บลิงก์สำหรับปุ่ม "Read Now"
  const primaryPdfUrl = useMemo(() => {
    const mediaArr = getList(item, "o:media");
    if (mediaArr.length === 0) return "";
    const firstId = mediaArr[0]?.["o:id"];
    return firstId ? `/read/${id}` : ""; // ไปหน้า Reader ที่คุณมีอยู่แล้ว
  }, [item, id]);

  if (loading) {
    return (
      <div className="min-h-screen bg-[#faf7f2]">
        <SiteHeader />
        <div className="max-w-7xl mx-auto px-6 py-24">
          <div className="h-8 w-40 bg-[#e7d8c9] rounded mb-4 animate-pulse" />
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="h-[60vh] bg-[#efe4d9] rounded-3xl animate-pulse" />
            <div className="space-y-3">
              <div className="h-7 w-3/4 bg-[#efe4d9] rounded animate-pulse" />
              <div className="h-7 w-2/3 bg-[#efe4d9] rounded animate-pulse" />
              <div className="h-40 w-full bg-[#efe4d9] rounded animate-pulse" />
            </div>
          </div>
        </div>
        <Footer />
      </div>
    );
  }

  if (err || !item) {
    return (
      <div className="min-h-screen bg-[#faf7f2]">
        <SiteHeader />
        <main className="max-w-3xl mx-auto px-6 py-16 text-center">
          <h1 className="text-2xl font-bold text-red-700 mb-2">เกิดข้อผิดพลาด</h1>
          <p className="text-[#7b6c61]">{err || "ไม่พบข้อมูล"}</p>
          <Link to="/" className="mt-6 inline-block text-[#d8653b] underline">กลับหน้าแรก</Link>
        </main>
        <Footer />
      </div>
    );
  }

  return (
    <div className="min-h-screen text-[#111518] bg-[#faf7f2]">
      <SiteHeader />

      {/* HERO: ปกซ้าย รายละเอียดขวา (ปก sticky ให้ความรู้สึก parallax) */}
      <section className="pt-28 pb-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-8">
          {/* breadcrumb */}
          <nav className="text-sm text-[#7b6c61] mb-4">
            <Link to="/" className="hover:underline">หน้าแรก</Link>
            <span className="mx-2">/</span>
            <Link to="/books" className="hover:underline">แนะนำหนังสือ</Link>
            <span className="mx-2">/</span>
            <span className="text-[#5b4a3e]">{meta.title}</span>
          </nav>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6 items-start">
            <div className="md:sticky md:top-24">
              <div className="rounded-3xl overflow-hidden shadow-xl ring-1 ring-[#e7d8c9] bg-[#fffaf3]">
                <img
                  src={normalizeHttps(meta.thumb)}
                  alt={meta.title}
                  className="w-full h-full object-cover"
                />
              </div>
            </div>

            <div>
              <div className="rounded-3xl shadow-lg border border-[#e7d8c9]/70 bg-[#fffaf3]/90 backdrop-blur p-6 md:p-8">
                <h1 className="text-3xl md:text-4xl font-extrabold tracking-tight text-[#5b4a3e] mb-3">
                  {meta.title}
                </h1>
                <ul className="space-y-1 text-[15px] text-[#57493f]">
                  <li><span className="opacity-70">ผู้แต่ง:</span> {meta.creator}</li>
                  <li><span className="opacity-70">ปี/วันที่:</span> {meta.date}</li>
                  {meta.extent && (<li><span className="opacity-70">จำนวนหน้า:</span> {meta.extent}</li>)}
                  {meta.identifier && (<li><span className="opacity-70">เลขทะเบียน/ลิงก์:</span> {meta.identifier}</li>)}
                  {meta.type && (<li><span className="opacity-70">ประเภท:</span> {meta.type}</li>)}
                  {meta.format && (<li><span className="opacity-70">ฟอร์แมต:</span> {meta.format}</li>)}
                  {meta.publisher && (<li><span className="opacity-70">สำนักพิมพ์:</span> {meta.publisher}</li>)}
                  {meta.language && (<li><span className="opacity-70">ภาษา:</span> {meta.language}</li>)}
                  {meta.rights && (<li><span className="opacity-70">สิทธิ์:</span> {meta.rights}</li>)}
                </ul>

                <div className="mt-6 flex gap-3 flex-wrap">
                  {primaryPdfUrl && (
                    <Link
                      to={primaryPdfUrl}
                      className="inline-flex items-center px-4 py-2 rounded-xl bg-[#d8653b] text-white shadow hover:opacity-95 transition"
                    >
                      Read Now
                    </Link>
                  )}
                  <Link
                    to={`/articles/${id}`}
                    className="inline-flex items-center px-4 py-2 rounded-xl bg-white text-[#5b4a3e] ring-1 ring-[#e7d8c9] shadow-sm hover:bg-[#fff7ee]"
                  >
                    เปิดบทความแบบอ่านยาว
                  </Link>
                </div>

                {/* หมายเหตุ: ไม่แสดง description ในกล่องรายละเอียดตามโจทย์ */}
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* DESCRIPTION SECTIONS: สลับฟันปลา + สลับตำแหน่งปกซ้าย/ขวา เมื่อเลื่อนลง */}
      <main className="pb-24">
        <div className="max-w-7xl mx-auto px-4 sm:px-8 space-y-14">
          {descriptions.length === 0 && (
            <p className="text-center text-[#7b6c61]">ยังไม่มีคำอธิบายสำหรับรายการนี้</p>
          )}

          {descriptions.map((desc, idx) => {
            const even = idx % 2 === 0; // idx 0: ปกขวา/เนื้อหาซ้าย เพื่อให้รู้สึกว่าปก "ย้ายด้าน" หลังจาก Hero
            return (
              <section
                key={idx}
                className="grid grid-cols-1 md:grid-cols-2 gap-6 items-center"
              >
                {/* เนื้อหา */}
                <div className={even ? "order-2 md:order-1" : "order-2 md:order-2"}>
                  <div className="bg-white/95 border border-[#e7d8c9] rounded-3xl shadow-md p-6 md:p-8 leading-8 text-[#3f342d]">
                    <h2 className="text-xl font-semibold text-[#5b4a3e] mb-3">
                      คำอธิบาย {descriptions.length > 1 ? `(${idx + 1})` : ""}
                    </h2>
                    <p className="whitespace-pre-wrap">{desc}</p>
                  </div>
                </div>

                {/* ปก: สลับด้านเพื่อให้เกิด motion ทางสายตาเหมือนภาพเลื่อนไปอีกฝั่ง */}
                <div className={even ? "order-1 md:order-2" : "order-1 md:order-1"}>
                  <div className="rounded-3xl overflow-hidden shadow-xl ring-1 ring-[#e7d8c9] bg-[#fffaf3] md:sticky md:top-24">
                    <img
                      src={normalizeHttps(meta.thumb)}
                      alt={`${meta.title} (cover)`}
                      className="w-full h-full object-cover"
                    />
                  </div>
                </div>
              </section>
            );
          })}
        </div>
      </main>

      <Footer />
      <BackToTop />
    </div>
  );
}
