export default function BooksPage() {
  // ... states อื่น ๆ คงเดิมได้
  const [items, setItems]   = useState([]);
  const [loading, setLoading] = useState(true);
  const [err, setErr]       = useState("");
  const [query, setQuery]   = useState("");

  // server-side pagination
  const [page, setPage]     = useState(1);
  const [perPage, setPerPage] = useState(12);
  const [total, setTotal]   = useState(null); // อาจเป็น null ถ้าอ่าน header ไม่ได้

  // sorting (ถ้าอยากให้เรียงชื่อ: "dcterms:title", "asc")
  const [sortBy, setSortBy] = useState("created");
  const [sortOrder, setSortOrder] = useState("desc");

  useEffect(() => {
    (async () => {
      try {
        setLoading(true);
        const { data, total } = await fetchItemsPage({ 
          q: query, page, perPage, sortBy, sortOrder 
        });
        setItems(data);
        setTotal(total); // อาจเป็น null
        setErr("");
      } catch (e) {
        setErr(e?.message || "โหลดข้อมูลไม่สำเร็จ");
      } finally {
        setLoading(false);
      }
    })();
  }, [query, page, perPage, sortBy, sortOrder]);

  const onSubmitSearch = (e) => {
    e?.preventDefault?.();
    setPage(1); // เริ่มที่หน้า 1 ทุกครั้งที่ค้นหาใหม่
  };

  // ... UI ส่วน HERO ให้เรียก onSubmitSearch และ setQuery ตามเดิม

  // ===== Pagination UI (แนะนำให้แยกเป็นคอมโพเนนต์ได้) =====
  const totalPages = total ? Math.max(1, Math.ceil(total / perPage)) : null;
  const canPrev = page > 1;
  const canNext = totalPages ? page < totalPages : items.length === perPage; 
  // กรณีไม่รู้ total: อนุมานว่าไปต่อได้ถ้าหน้านี้ยังเต็ม per_page

  return (
    <div className="min-h-screen bg-[#fbf6ed] text-[#5b4a3e]">
      {/* HERO */}
      <section className="relative w-full h-[360px] md:h-[420px] overflow-hidden">
        <img src="/assets/hero-books.jpg" alt="banner" className="absolute inset-0 w-full h-full object-cover" />
        <div className="absolute inset-0 bg-black/20" />
        <div className="relative z-10 h-full flex items-center justify-center">
          <form className="flex w-[90%] max-w-2xl" onSubmit={onSubmitSearch}>
            <input
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              className="flex-1 h-12 md:h-14 px-4 rounded-l-full bg-white/95 focus:outline-none"
              placeholder="ค้นหาเอกสารตัวเขียน…"
            />
            <button type="submit" className="px-5 md:px-6 rounded-r-full bg-[#f08a24] text-white font-bold">
              ค้นหา
            </button>
          </form>
        </div>
      </section>

      {/* … breadcrumb + ชื่อหน้า + sidebar + grid (ยังใช้ items จาก server) … */}

      {/* Pagination แสดงจำนวนรวมถ้าทราบ */}
      <div className="max-w-7xl mx-auto px-4 sm:px-8">
        <div className="flex items-center justify-between gap-4 py-4">
          <div className="text-sm text-[#8e6f5a]">
            {total != null ? `พบผลลัพธ์ทั้งหมด ${total.toLocaleString("th-TH")} รายการ` : "ค้นหาแบบแบ่งหน้า"}
          </div>

          <div className="flex items-center gap-3">
            <select
              value={perPage}
              onChange={(e) => { setPerPage(Number(e.target.value)); setPage(1); }}
              className="border border-[#f1e9de] bg-white px-2 py-1 rounded text-sm"
            >
              {[9,12,18,24].map(n => <option key={n} value={n}>{n}/หน้า</option>)}
            </select>
            <select
              value={`${sortBy}|${sortOrder}`}
              onChange={(e) => {
                const [sb, so] = e.target.value.split("|");
                setSortBy(sb); setSortOrder(so); setPage(1);
              }}
              className="border border-[#f1e9de] bg-white px-2 py-1 rounded text-sm"
            >
              <option value="created|desc">ใหม่ล่าสุด</option>
              <option value="created|asc">เก่าสุด</option>
              <option value="dcterms:title|asc">ชื่อ (ก→ฮ)</option>
              <option value="dcterms:title|desc">ชื่อ (ฮ→ก)</option>
            </select>
          </div>
        </div>

        {/* ปุ่มเพจ + เลขหน้า ถ้ารู้ total */}
        <div className="flex items-center justify-center gap-2 py-6">
          <button
            className="px-3 py-1.5 border border-[#f1e9de] rounded hover:bg-[#fff7ef] disabled:opacity-50"
            onClick={() => setPage((p) => Math.max(1, p - 1))}
            disabled={!canPrev}
          >
            {"<"}
          </button>

          {totalPages ? (
            Array.from({ length: Math.min(7, totalPages) }).map((_, i) => {
              // สร้างหน้าต่างเลขหน้าอย่างง่าย (คุณสามารถทำ windowed pagination ตามเดิมได้)
              const n = i + Math.max(1, Math.min(page - 3, totalPages - 6));
              return (
                <button
                  key={n}
                  className={`px-3 py-1.5 border rounded ${
                    n === page ? "bg-[#d8653b] border-[#d8653b] text-white" : "border-[#f1e9de] hover:bg-[#fff7ef]"
                  }`}
                  onClick={() => setPage(n)}
                >
                  {n}
                </button>
              );
            })
          ) : (
            <span className="text-sm text-[#8e6f5a]">กำลังเรียกทีละหน้า…</span>
          )}

          <button
            className="px-3 py-1.5 border border-[#f1e9de] rounded hover:bg-[#fff7ef] disabled:opacity-50"
            onClick={() => setPage((p) => p + 1)}
            disabled={!canNext}
          >
            {">"}
          </button>
        </div>
      </div>
    </div>
  );
}
