// src/pages/HomePage.jsx
import React, { useEffect, useMemo, useState } from "react";
import "./HomePage.css";

import SiteHeader from "../components/SiteHeader";
import ParallaxHero from "../components/ParallaxHero";
import Footer from "../components/Footer";
import AutoCarousel from "../components/AutoCarousel";

import { useNavigate } from "react-router-dom";
import { useTranslation } from "react-i18next";
import i18n from "../i18n";
import { fetchItemsLite } from "../lib/omekaClient";
import { pickLang } from "../lib/i18nPick";
import { thumbUrlOf } from "../lib/omekaClient"; // ✅ ใช้ดึงรูปปก

/** ===== ticker ชื่อ (เก็บไว้ใช้ได้ภายหลัง) ===== */
function TitleTicker({ items = [], titleOfItem, onClick }) {
  if (!items?.length) {
    return <div className="w-full py-2 text-center text-sm text-black/50">—</div>;
  }
  return (
    <div className="w-full overflow-x-auto whitespace-nowrap no-scrollbar border-t border-black/10 bg-white/60">
      <ul className="flex gap-4 px-3 py-2">
        {items.map((it) => (
          <li
            key={it["o:id"]}
            className="text-sm sm:text-[15px] hover:underline cursor-pointer text-[#5b4a3e]"
            onClick={() => onClick?.(it)}
            title={titleOfItem(it)}
          >
            {titleOfItem(it)}
          </li>
        ))}
      </ul>
    </div>
  );
}

/** ===== helpers เอาค่าจาก fields ที่ไม่ใช่ description ===== */
const getVals = (it, prop) =>
  Array.isArray(it?.[prop]) ? it[prop].map((v) => v?.["@value"]).filter(Boolean) : [];

const getLangVals = (it, prop, lang) =>
  Array.isArray(it?.[prop])
    ? it[prop]
        .map((v) => (v?.["@language"] ? (v["@language"] === lang ? v["@value"] : null) : v?.["@value"]))
        .filter(Boolean)
    : [];

export default function HomePage() {
  const { t } = useTranslation();
  const navigate = useNavigate();

  // ชุดข้อมูลตามหมวด
  const [books, setBooks] = useState([]);
  const [manuscripts, setManuscripts] = useState([]);
  const [articles, setArticles] = useState([]);

  const [query, setQuery] = useState("");
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState("");

  // helpers: multi-lang
  const titleOfItem = (it) => pickLang(it?.["dcterms:title"], i18n.language) || "";
  const descOfItem = (it) => pickLang(it?.["dcterms:description"], i18n.language) || "";

  const openPDF = (item) => {
    const id = item?.["o:id"];
    if (!id) return alert(t("errors.noId", "ไม่พบรหัสรายการ"));
    navigate(`/read/${id}`);
  };

  useEffect(() => {
    // ปรับ backgroundAttachment สำหรับจอเล็ก (iOS Safari)
    const mq = window.matchMedia("(max-width: 767px)");
    const apply = () =>
      document.documentElement.style.setProperty("--bg-attach", mq.matches ? "scroll" : "fixed");
    apply();
    mq.addEventListener?.("change", apply);
    return () => mq.removeEventListener?.("change", apply);
  }, []);

  useEffect(() => {
    (async () => {
      try {
        setLoading(true);
        setErr("");

        // ดึงข้อมูลตามคลาส
        const [bks, manus, arts] = await Promise.all([
          fetchItemsLite({
            limit: 18,
            sortBy: "created",
            sortOrder: "desc",
            resource_class_label: "Book",
          }),
          fetchItemsLite({
            limit: 12,
            sortBy: "created",
            sortOrder: "desc",
            resource_class_label: "Manuscript",
          }),
          fetchItemsLite({
            limit: 12,
            sortBy: "created",
            sortOrder: "desc",
            resource_class_label: "Article",
          }),
        ]);

        setBooks(Array.isArray(bks) ? bks : []);
        setManuscripts(Array.isArray(manus) ? manus : []);
        setArticles(Array.isArray(arts) ? arts : []);
      } catch (e) {
        setErr(e?.message || t("errors.loadFailed", "โหลดข้อมูลไม่สำเร็จ"));
      } finally {
        setLoading(false);
      }
    })();
  }, [i18n.language]);

  // ค้นหาในหน้า (filter ชุดข้อมูลทั้งหมด)
  const filterByQuery = (arr) => {
    if (!query.trim()) return arr || [];
    const q = query.toLowerCase();
    return (arr || []).filter((it) => {
      const t0 = (titleOfItem(it) || "").toLowerCase();
      const d0 = (descOfItem(it) || "").toLowerCase();
      return t0.includes(q) || d0.includes(q);
    });
  };

  const booksFiltered = useMemo(
    () => filterByQuery(books).slice(0, 18),
    [books, query, i18n.language]
  );
  const manuscriptsFiltered = useMemo(
    () => filterByQuery(manuscripts).slice(0, 12),
    [manuscripts, query, i18n.language]
  );
  const articlesFiltered = useMemo(
    () => filterByQuery(articles).slice(0, 12),
    [articles, query, i18n.language]
  );

  // เลือก Manuscript ตัวแรกเป็น "รายการแนะนำ"
  const featuredManuscript = manuscriptsFiltered[0];

  // รูปปกของรายการแนะนำ
  const [featuredThumb, setFeaturedThumb] = useState(null);
  useEffect(() => {
    let alive = true;
    (async () => {
      if (!featuredManuscript) {
        if (alive) setFeaturedThumb(null);
        return;
      }
      const u = await thumbUrlOf(featuredManuscript);
      if (alive) setFeaturedThumb(u);
    })();
    return () => {
      alive = false;
    };
  }, [featuredManuscript]);

  // เตรียม fields สำหรับรายละเอียด (ไม่ใช้ description)
  const manuFields = useMemo(() => {
    const it = featuredManuscript || {};
    const lang = i18n.language;

    // รวมค่าที่เป็น array ให้สวย ๆ
    const joiner = (arr) => (arr && arr.length ? arr.join(", ") : "—");

    return [
      { label: "ผู้สร้าง/ผู้เขียน", value: joiner(getLangVals(it, "dcterms:creator", lang) || getVals(it, "dcterms:creator")) },
      { label: "ปีที่จัดทำ/เผยแพร่", value: joiner(getVals(it, "dcterms:date")) },
      { label: "ภาษา", value: joiner(getVals(it, "dcterms:language")) },
      { label: "หัวเรื่อง", value: joiner(getLangVals(it, "dcterms:subject", lang) || getVals(it, "dcterms:subject")) },
      { label: "สถานที่", value: joiner(getLangVals(it, "dcterms:spatial", lang) || getVals(it, "dcterms:spatial")) },
      { label: "สำนักพิมพ์/ผู้ให้ข้อมูล", value: joiner(getLangVals(it, "dcterms:publisher", lang) || getVals(it, "dcterms:publisher")) },
      { label: "รหัสรายการ", value: it?.["o:id"] ?? "—" },
    ];
  }, [featuredManuscript, i18n.language]);

  return (
    <div
      className="min-h-screen text-[#111518]"
      style={{
        backgroundImage: "url('/assets/banner.webp')",
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundAttachment: "var(--bg-attach, fixed)",
      }}
    >
      <SiteHeader />

      {/* Banner + Search */}
      <ParallaxHero
        banner=""
        onSearch={setQuery}
        searchPlaceholder={t("search.placeholder", "ค้นหาหนังสือ/คำอธิบาย…")}
      />

      {/* ===== Layout ใหม่: 3 columns × 2 rows ===== */}
      <main className="max-w-7xl mx-auto px-4 sm:px-8 lg:px-12 py-8 sm:py-12">
        <div className="grid grid-cols-1 lg:grid-cols-3 lg:grid-rows-2 gap-6 sm:gap-8">
          {/* ซ้ายบน: Manuscript (2 คอลัมน์) → ภาพ 1 คอลัมน์ + รายละเอียด 1 คอลัมน์ */}
          <section className="lg:col-span-2 lg:row-start-1 card-soft">
            <div className="px-4 pt-6">
              <h2 className="text-2xl sm:text-3xl font-bold text-[#5b4a3e] text-center">
                แนะนำเอกสารโบราณ (Manuscript)
              </h2>
            </div>

            {/* กล่องภายใน 2 คอลัมน์: ซ้ายรูป / ขวา details */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6 px-4 pb-6 items-start">
              {/* ซ้าย: รูปเดียว เต็มกล่อง */}
              <button
                onClick={() => featuredManuscript && openPDF(featuredManuscript)}
                className="relative w-full aspect-[3/4] overflow-hidden rounded-xl bg-white/70 hover:bg-white transition shadow-sm"
                title={featuredManuscript ? titleOfItem(featuredManuscript) : ""}
              >
                {featuredThumb ? (
                  <img
                    src={featuredThumb}
                    alt=""
                    className="w-full h-full object-cover object-center"
                    loading="lazy"
                  />
                ) : (
                  <div className="w-full h-full grid place-items-center text-black/40 text-sm">
                    ไม่มีภาพ
                  </div>
                )}
              </button>

              {/* ขวา: รายละเอียด (ไม่ใช้ description) */}
              <div className="rounded-xl bg-white/70 p-4 shadow-sm">
                <div className="font-semibold text-lg text-[#5b4a3e]">
                  {featuredManuscript ? titleOfItem(featuredManuscript) : "—"}
                </div>

                <dl className="mt-3 space-y-2 text-sm text-black/80">
                  {manuFields.map((f, idx) => (
                    <div key={idx} className="grid grid-cols-3 gap-2">
                      <dt className="col-span-1 text-black/60">{f.label}</dt>
                      <dd className="col-span-2">{f.value || "—"}</dd>
                    </div>
                  ))}
                </dl>

                <div className="mt-4">
                  <button
                    onClick={() => featuredManuscript && openPDF(featuredManuscript)}
                    className="px-4 py-2 rounded-lg bg-[#d8653b] text-white hover:opacity-90 transition"
                  >
                    เปิดอ่าน
                  </button>
                </div>
              </div>
            </div>

            {/* ถ้าอยากโชว์รายชื่อเลื่อนด้านล่าง เปิดส่วนนี้ได้ */}
            {/* <TitleTicker
              items={loading || err ? [] : manuscriptsFiltered}
              titleOfItem={titleOfItem}
              onClick={openPDF}
            /> */}
          </section>

          {/* ขวา: Book (กิน 1 คอลัมน์, 2 แถวแนวตั้ง) */}
          <aside className="lg:col-start-3 lg:row-span-2">
            <AutoCarousel
              title="แนะนำหนังสืออัปใหม่ (Book)"
              items={loading || err ? [] : booksFiltered}
              onOpen={openPDF}
              className="h-[760px] sm:h-[860px] lg:h-[900px]" // ให้สูงพอกับสองบล็อกซ้ายรวมกัน
            />
          </aside>

          {/* ซ้ายล่าง: Article (กิน 2 คอลัมน์, แถว 2) */}
          <section className="lg:col-span-2 lg:row-start-2 card-soft flex flex-col">
            <div className="px-4 pt-6">
              <h2 className="text-2xl sm:text-3xl font-bold text-[#5b4a3e] text-center">
                บทความ / กิจกรรม
              </h2>
              <p className="text-center mt-2 text-sm sm:text-base text-black/70">
                โชว์รายการล่าสุดจากคลาส <strong>Article</strong>
              </p>
            </div>

            <div className="px-4 pb-6">
              <div className="mt-4 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
                {(loading || err ? [] : articlesFiltered.slice(0, 6)).map((it) => (
                  <button
                    key={it["o:id"]}
                    onClick={() => openPDF(it)}
                    className="group border rounded-xl p-4 bg-white/70 hover:bg-white text-left transition shadow-sm"
                    title={titleOfItem(it)}
                  >
                    <div className="font-semibold text-[#5b4a3e] line-clamp-2 group-hover:underline">
                      {titleOfItem(it) || "—"}
                    </div>
                    <div className="mt-2 text-xs text-black/60 line-clamp-3">
                      {descOfItem(it) || ""}
                    </div>
                  </button>
                ))}
              </div>
            </div>
          </section>
        </div>
      </main>

      <Footer />
    </div>
  );
}
