// src/pages/BookArticlesPage.jsx
import React, { useEffect, useMemo, useRef, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import SiteHeader from "../components/SiteHeader";
import BackToTop from "../components/BackToTop";
import { fetchItemsLite, titleOf, descOf } from "../lib/omekaClient";

/* helpers */
const toHttps = (u) =>
  u ? (u.startsWith("http://") ? u.replace(/^http:/, "https:") : u) : "";
const stripTags = (html = "") => {
  if (!html) return "";
  const tmp = document.createElement("div");
  tmp.innerHTML = html;
  return (tmp.textContent || tmp.innerText || "").trim();
};

/* === พาเลตต์พื้นหลัง + สีข้อความจับคู่ === */
const BG_PALETTE = [
  { bg: "#ff8a3d", text: "#ffffff" }, // citrus — สดใส/ข้อความขาว
  { bg: "#f6d4b1", text: "#5b4a3e" }, // cream — นุ่ม/ข้อความน้ำตาลเข้ม
  { bg: "#f0c2a8", text: "#5b4a3e" }, // peach
  { bg: "#ffd7a0", text: "#5b4a3e" }, // light orange
  { bg: "#ffe9d6", text: "#5b4a3e" }, // very light
];

/* ============ Main Page ============ */
export default function BookArticlesPage() {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    (async () => {
      try {
        const res = await fetchItemsLite();
        const filtered = (res || []).filter(
          (it) =>
            Array.isArray(it["dcterms:description"]) &&
            it["dcterms:description"].length > 0
        );
        setBooks(filtered);
      } catch (e) {
        console.error("โหลดรายการล้มเหลว", e);
      } finally {
        setLoading(false);
      }
    })();
  }, []);

  if (loading) {
    return (
      <div className="min-h-screen flex flex-col bg-[#faf7f2]">
        <SiteHeader />
        <div className="flex-1 grid place-items-center text-[#7b6c61]">
          กำลังโหลด…
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-[#faf7f2] text-[#111518]">
      <SiteHeader />
      <FullscreenHeroCarousel items={books} />
      <BackToTop />
    </div>
  );
}

/* ============ Carousel Section ============ */
function FullscreenHeroCarousel({ items = [] }) {
  const trackRef = useRef(null);
  const [index, setIndex] = useState(0);
  const navigate = useNavigate();
  const slideH = "calc(100vh - 72px)"; // ลบความสูง header

  const onScroll = () => {
    const el = trackRef.current;
    if (!el) return;
    const i = Math.round(el.scrollLeft / el.clientWidth);
    setIndex(i);
  };
  const onWheel = (e) => {
    const el = trackRef.current;
    if (!el) return;
    if (Math.abs(e.deltaY) > Math.abs(e.deltaX)) {
      e.preventDefault();
      el.scrollLeft += e.deltaY;
    }
  };
  const scrollTo = (i) => {
    const el = trackRef.current;
    if (!el) return;
    const next = Math.max(0, Math.min(items.length - 1, i));
    el.scrollTo({ left: next * el.clientWidth, behavior: "smooth" });
    setIndex(next);
  };

  useEffect(() => {
    const h = (e) => {
      if (e.key === "ArrowRight") scrollTo(index + 1);
      if (e.key === "ArrowLeft") scrollTo(index - 1);
    };
    window.addEventListener("keydown", h);
    return () => window.removeEventListener("keydown", h);
  }, [index]);

  const slides = useMemo(
    () =>
      items.map((book, i) => {
        const id = book["o:id"];
        const title = titleOf(book) || `Item #${id}`;
        const desc = (stripTags(descOf(book) || "").slice(0, 220) || "") + "…";
        const rawThumb =
          book?.thumbnail_display_urls?.large ||
          book?.thumbnail_display_urls?.medium ||
          book?.["o:thumbnail_urls"]?.large ||
          book?.["o:thumbnail_urls"]?.medium ||
          "";
        const cover = toHttps(rawThumb) || "/assets/placeholder.webp";

        const palette = BG_PALETTE[i % BG_PALETTE.length];
        const { bg, text } = palette;
        const isLight = text === "#ffffff";

        return (
          <section
            key={id}
            className="snap-start shrink-0 w-full flex flex-col justify-center items-center"
            style={{
              height: slideH,
              background: bg,
              color: text,
              padding: "2rem 1rem",
            }}
          >
            <div className="w-full max-w-[1100px] mx-auto grid md:grid-cols-2 gap-8 items-center">
              {/* ซ้าย: ปก */}
              <div className="flex justify-center md:justify-end">
                <div className="relative w-[70%] md:w-[85%] lg:w-[75%] aspect-[3/4]">
                  <img
                    src={cover}
                    alt={title}
                    className="absolute inset-0 w-full h-full object-contain drop-shadow-2xl"
                    loading="lazy"
                  />
                  <div className="absolute -bottom-6 inset-x-0 mx-auto h-10 w-4/5 rounded-full bg-black/20 blur-xl opacity-40" />
                </div>
              </div>

              {/* ขวา: เนื้อหา */}
              <div className="text-center md:text-left px-4">
                <h2
                  className="font-extrabold leading-tight"
                  style={{
                    fontSize: "clamp(2rem, 4vw + 1rem, 5rem)",
                    color: text,
                  }}
                >
                  {title}
                </h2>
                <p
                  className="mt-5 max-w-xl mx-auto md:mx-0"
                  style={{
                    color: isLight ? "rgba(255,255,255,0.9)" : "rgba(91,74,62,0.85)",
                    fontSize: "clamp(1rem, 0.8vw + 0.9rem, 1.25rem)",
                  }}
                >
                  {desc}
                </p>
                <div className="mt-8 flex flex-wrap gap-3 justify-center md:justify-start">
                  <button
                    onClick={() => navigate(`/book/${id}`)}
                    className={`inline-flex items-center h-12 px-6 rounded-full font-semibold transition shadow ${
                      isLight
                        ? "bg-white text-[#5b4a3e] hover:opacity-90"
                        : "bg-[#d8653b] text-white hover:opacity-90"
                    }`}
                  >
                    Discover →
                  </button>
                  <Link
                    to={`/read/${id}`}
                    className={`inline-flex items-center h-12 px-6 rounded-full ring-1 transition ${
                      isLight
                        ? "bg-white/20 text-white ring-white/40 hover:bg-white/25"
                        : "bg-transparent text-[#5b4a3e] ring-[#5b4a3e]/30 hover:bg-[#5b4a3e]/5"
                    }`}
                  >
                    Read now
                  </Link>
                </div>
              </div>
            </div>
          </section>
        );
      }),
    [items]
  );

  return (
    <div className="relative">
      <div
        ref={trackRef}
        onScroll={onScroll}
        onWheel={onWheel}
        className="snap-x snap-mandatory overflow-x-auto overflow-y-hidden w-full h-[calc(100vh-72px)]
                   [scrollbar-width:none] [-ms-overflow-style:none] [&::-webkit-scrollbar]:hidden touch-pan-x"
      >
        <div className="flex w-max h-full">{slides}</div>
      </div>

      {/* ปุ่มลูกศร */}
      <div className="pointer-events-none absolute inset-y-0 left-3 right-3 hidden md:flex items-center justify-between">
        <button
          className="pointer-events-auto grid place-items-center h-11 w-11 rounded-full bg-white shadow ring-1 ring-black/10 hover:opacity-90 transition"
          onClick={() => scrollTo(index - 1)}
        >
          ‹
        </button>
        <button
          className="pointer-events-auto grid place-items-center h-11 w-11 rounded-full bg-white shadow ring-1 ring-black/10 hover:opacity-90 transition"
          onClick={() => scrollTo(index + 1)}
        >
          ›
        </button>
      </div>

      {/* จุดบอกตำแหน่ง */}
      <div className="absolute left-1/2 -translate-x-1/2 bottom-6 flex gap-2">
        {items.map((_, i) => (
          <button
            key={i}
            onClick={() => scrollTo(i)}
            className={`h-2.5 rounded-full transition-all ${
              index === i ? "w-8 bg-white" : "w-2.5 bg-white/60 hover:bg-white"
            }`}
          />
        ))}
      </div>
    </div>
  );
}
