// src/pages/RootApp.js
import React, { Suspense } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";

// ✅ i18n & Settings อยู่คนละโฟลเดอร์กับไฟล์นี้ จึงใช้ "../"
import "../i18n/index.js";
import SettingsProvider from "../context/SettingsContext.jsx";

// ✅ เพจอื่น ๆ ที่อยู่ "โฟลเดอร์เดียวกัน" ใช้ "./"
import HomePage from "./HomePage.jsx";
import BooksPage from "./BooksPage.js";           // ← ไฟล์คุณเป็น .js
import BookArticlesPage from "./BookArticlesPage.jsx";
import BookArticleDetail from "./BookArticleDetail.jsx";
import ReaderPage from "./ReaderPage.jsx";

export default function RootApp() {
  return (
    <SettingsProvider>
      <Suspense fallback={<div className="p-6 text-center text-gray-600">Loading…</div>}>
        <BrowserRouter>
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/books" element={<BooksPage />} />
            <Route path="/read/:id" element={<ReaderPage />} />
            <Route path="/articles" element={<BookArticlesPage />} />
            <Route path="/book/:id" element={<BookArticleDetail />} />
          </Routes>
        </BrowserRouter>
      </Suspense>
    </SettingsProvider>
  );
}
