import React, { useState, useEffect } from 'react';
import axios from 'axios';

// ใช้ environment variables เพื่อซ่อน API keys
const BASE_URL =
  process.env.REACT_APP_API_BASE_URL ||
  'https://z277970-rr25o5.ps07.zwhhosting.com/omekas/api';

// ===== Helper: ต่อ query string ของ API key =====
const withKeys = (url) => {
  const key_identity = process.env.REACT_APP_API_KEY_IDENTITY;
  const key_credential = process.env.REACT_APP_API_KEY_CREDENTIAL;
  const sep = url.includes('?') ? '&' : '?';
  return `${url}${sep}key_identity=${key_identity}&key_credential=${key_credential}`;
};

// ===== Helper: ดึง JSON ผ่าน proxy หลายตัว (allorigins -> codetabs -> ตรง) =====
const fetchJsonWithProxies = async (finalUrl) => {
  const masked = finalUrl
    .replace(process.env.REACT_APP_API_KEY_IDENTITY || '', '***')
    .replace(process.env.REACT_APP_API_KEY_CREDENTIAL || '', '***');
  console.log('Fetch JSON via proxies:', masked);

  // 1) allorigins
  try {
    const viaAllorigins = `https://api.allorigins.win/get?url=${encodeURIComponent(finalUrl)}`;
    const r1 = await axios.get(viaAllorigins, { timeout: 15000 });
    return JSON.parse(r1.data.contents);
  } catch (e1) {
    console.log('allorigins failed -> try codetabs');
  }

  // 2) codetabs
  try {
    const viaCodetabs = `https://api.codetabs.com/v1/proxy?quest=${encodeURIComponent(finalUrl)}`;
    const r2 = await axios.get(viaCodetabs, { timeout: 15000 });
    return r2.data;
  } catch (e2) {
    console.log('codetabs failed -> try direct');
  }

  // 3) direct (ถ้า CORS อนุญาต)
  const r3 = await axios.get(finalUrl, { timeout: 15000 });
  return r3.data;
};

function App() {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchItems();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const fetchItems = async () => {
    try {
      setLoading(true);

      if (
        !process.env.REACT_APP_API_KEY_IDENTITY ||
        !process.env.REACT_APP_API_KEY_CREDENTIAL
      ) {
        throw new Error('API keys not found. Please check your .env file.');
      }

      // ชี้ไปที่ /api/items (ตามโจทย์แบบที่ 2)
      const apiUrl = withKeys(`${BASE_URL}/items`);
      const data = await fetchJsonWithProxies(apiUrl);

      // data คือ array ของ items ที่มี thumbnail_display_urls อยู่ในแต่ละ item
      const arr = Array.isArray(data) ? data : [];
      setItems(arr);
      setError(null);
    } catch (err) {
      console.error('Error fetching items:', err);
      const msg = err?.response?.data || err.message || 'Unknown error';
      let errorMessage = 'ไม่สามารถโหลดรายการ Item ได้';

      if (msg.includes('API keys not found')) {
        errorMessage = 'ไม่พบ API keys กรุณาตรวจสอบไฟล์ .env';
      } else if (msg.includes('Network Error') || msg.includes('CORS')) {
        errorMessage =
          'ไม่สามารถเชื่อมต่อกับ API ได้ เนื่องจากปัญหา CORS หรือเครือข่าย';
      } else {
        errorMessage = `เกิดข้อผิดพลาด: ${msg}`;
      }
      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  // เปิดอ่าน PDF: ยิงไปที่ /api/media/{id} เพื่อดึง o:original_url แล้วเปิดแท็บใหม่
  const openItemPrimaryPdf = async (item) => {
    try {
      const mediaId = item?.['o:primary_media']?.['o:id'];
      if (!mediaId) throw new Error('ไม่พบไฟล์หลักของรายการนี้');

      const mediaUrl = withKeys(`${BASE_URL}/media/${mediaId}`);
      const media = await fetchJsonWithProxies(mediaUrl);

      const pdfUrl = media?.['o:original_url'];
      if (!pdfUrl) throw new Error('ไม่พบ URL ของไฟล์ PDF');
      window.open(pdfUrl, '_blank');
    } catch (err) {
      alert(err.message || 'เปิดไฟล์ไม่สำเร็จ');
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return '-';
    const date = new Date(dateString);
    return date.toLocaleDateString('th-TH', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
    });
  };

  if (loading) {
    return (
      <div className="container">
        <div className="loading">กำลังโหลดรายการหนังสือ...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container">
        <div className="error">
          <h3>เกิดข้อผิดพลาด</h3>
          <p>{error}</p>

          {error.includes('CORS') && (
            <div style={{ marginTop: '1rem', fontSize: '0.9rem', color: '#666' }}>
              <p><strong>คำแนะนำ:</strong></p>
              <ul style={{ textAlign: 'left', maxWidth: 420, margin: '0 auto' }}>
                <li>ลองรีเฟรชหน้าเว็บ</li>
                <li>ตรวจสอบการเชื่อมต่ออินเทอร์เน็ต</li>
                <li>ติดต่อผู้ดูแลระบบหาก API server มีปัญหา</li>
              </ul>
            </div>
          )}

          {error.includes('API keys') && (
            <div style={{ marginTop: '1rem', fontSize: '0.9rem', color: '#666' }}>
              <p><strong>วิธีแก้ไข:</strong></p>
              <p>สร้างไฟล์ .env และใส่ API keys ตามตัวอย่างใน .env.example</p>
            </div>
          )}
        </div>

        <button
          onClick={fetchItems}
          className="view-pdf-btn"
          style={{ maxWidth: 200, margin: '1rem auto', display: 'block' }}
        >
          ลองใหม่อีกครั้ง
        </button>
      </div>
    );
  }

  return (
    <div className="container">
      <header className="header">
        <h1>ห้องสมุดออนไลน์</h1>
        <p>รายการหนังสือ (จาก Omeka S Items)</p>
      </header>

      {items.length === 0 ? (
        <div className="no-data">ไม่พบรายการหนังสือ</div>
      ) : (
        <div className="pdf-grid">
          {items.map((item) => {
            const title =
              item['o:title'] ||
              item['dcterms:title']?.[0]?.['@value'] ||
              'ไม่มีชื่อเอกสาร';

            const createdISO = item?.['o:created']?.['@value'];
            const thumb =
              item?.thumbnail_display_urls?.medium ||
              item?.thumbnail_display_urls?.large ||
              item?.thumbnail_display_urls?.square ||
              null;

            return (
              <div key={item['o:id']} className="pdf-card">
                {/* Thumbnail จาก item */}
                {thumb && (
                  <img
                    src={thumb}
                    alt={title}
                    className="pdf-thumb"
                    loading="lazy"
                    onError={(e) => {
                      e.currentTarget.style.display = 'none';
                    }}
                    style={{
                      width: '100%',
                      height: 180,
                      objectFit: 'cover',
                      borderRadius: 12,
                      marginBottom: 12,
                    }}
                  />
                )}

                <h3 className="pdf-title">{title}</h3>

                <div className="pdf-info">
                  <div className="pdf-info-item">
                    <span className="pdf-info-label">วันที่สร้าง:</span>
                    <span className="pdf-date">{formatDate(createdISO)}</span>
                  </div>
                </div>

                <button
                  className="view-pdf-btn"
                  onClick={() => openItemPrimaryPdf(item)}
                >
                  เปิดอ่าน PDF
                </button>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
}

export default App;
