import React, { useEffect, useState, useMemo } from "react";
import axios from "axios";
import "./App.css";

/** ====== ENV & URL helpers ====== */
const RAW_BASE =
  process.env.REACT_APP_API_BASE_URL ||
  "";
const BASE_URL = RAW_BASE.replace(/\/+$/, "");

const withKeys = (url) => {
  const key_identity = process.env.REACT_APP_API_KEY_IDENTITY;
  const key_credential = process.env.REACT_APP_API_KEY_CREDENTIAL;
  const sep = url.includes("?") ? "&" : "?";
  return `${url}${sep}key_identity=${key_identity}&key_credential=${key_credential}`;
};
const api = (path) =>
  withKeys(`${BASE_URL}${path.startsWith("/") ? path : `/${path}`}`);

const fetchJsonWithProxies = async (finalUrl) => {
  try {
    const viaAllorigins = `https://api.allorigins.win/get?url=${encodeURIComponent(
      finalUrl
    )}`;
    const r1 = await axios.get(viaAllorigins, { timeout: 15000 });
    return JSON.parse(r1.data.contents);
  } catch {}
  try {
    const viaCodetabs = `https://api.codetabs.com/v1/proxy?quest=${encodeURIComponent(
      finalUrl
    )}`;
    const r2 = await axios.get(viaCodetabs, { timeout: 15000 });
    return r2.data;
  } catch {}
  const r3 = await axios.get(finalUrl, { timeout: 15000 });
  return r3.data;
};

/** ====== Small helpers ====== */
const titleOf = (item) =>
  item["o:title"] || item["dcterms:title"]?.[0]?.["@value"] || "ไม่มีชื่อเอกสาร";

const descOf = (item) =>
  item["dcterms:abstract"]?.[0]?.["@value"] ||
  item["dcterms:description"]?.[0]?.["@value"] ||
  "";

const thumbOf = (item) =>
  item?.thumbnail_display_urls?.large ||
  item?.thumbnail_display_urls?.medium ||
  item?.thumbnail_display_urls?.square ||
  null;

const createdOf = (item) => {
  const iso = item?.["o:created"]?.["@value"];
  if (!iso) return "-";
  const d = new Date(iso);
  return d.toLocaleDateString("th-TH", {
    year: "numeric",
    month: "long",
    day: "numeric",
  });
};

/** ====== UI Components (Tailwind) ====== */
function SiteHeader({ query, setQuery, onSearch }) {
  return (
    <header className="flex items-center justify-between border-b border-[#f0f3f4] dark:border-gray-800 px-4 sm:px-10 lg:px-40 py-3 bg-white dark:bg-background-dark">
      <div className="flex items-center gap-4 text-[#111518] dark:text-white">
        <div className="size-6 text-primary">
          <svg className="fill-current" viewBox="0 0 24 24">
            <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-1 17.93c-3.95-.49-7-3.85-7-7.93 0-.62.08-1.21.21-1.79L9 15v1c0 1.1.9 2 2 2v1.93zm6.9-2.54c-.26-.81-1-1.39-1.9-1.39h-1v-3c0-.55-.45-1-1-1H8v-2h2c.55 0 1-.45 1-1V7h2c1.1 0 2-.9 2-2v-.41c2.93 1.19 5 4.06 5 7.41 0 2.08-.8 3.97-2.1 5.39z" />
          </svg>
        </div>
        <h2 className="text-xl font-bold font-display leading-tight tracking-[-0.015em]">
          The Culture Read @CNX
        </h2>
      </div>

      <div className="hidden md:flex flex-1 justify-end gap-8">
        <nav className="flex items-center gap-9">
          <a className="navlink" href="#">Home</a>
          <a className="navlink" href="#">Manuscripts</a>
          <a className="navlink" href="#">Collections</a>
          <a className="navlink" href="#">Articles</a>
          <a className="navlink" href="#">About</a>
        </nav>

        <div className="flex items-center gap-2">
          <button className="btn-neutral min-w-[84px] h-10 px-4">Language</button>
          <div className="flex items-center gap-1">
            <button className="icon-neutral">A-</button>
            <button className="icon-neutral">A</button>
            <button className="icon-neutral">A+</button>
          </div>
        </div>
      </div>

      <button className="md:hidden p-2 rounded-lg hover:bg-gray-200 dark:hover:bg-gray-700">
        <span className="material-symbols-outlined text-[#111518] dark:text-white">
          menu
        </span>
      </button>
    </header>
  );
}

function Hero({ query, setQuery, onSearch }) {
  return (
    <section className="px-4 sm:px-10 lg:px-40 py-5">
      <div className="max-w-[960px] mx-auto">
        <div className="min-h-[480px] rounded-xl bg-cover bg-center bg-no-repeat flex flex-col items-center justify-center gap-6 p-4"
          style={{
            backgroundImage:
              'linear-gradient(rgba(0,0,0,.4), rgba(0,0,0,.7)), url("https://lh3.googleusercontent.com/aida-public/AB6AXuAebZnQLGxo9UTQjfOX2E5IBtetRmpAjFSQJZaE92yIDKaV0dR3DqsHme7yl6DcdsjLKmd7dGMLSdIzWzrxSuqNKePD_nUCB4duI-KEdljPKDEwfCyYB4KiR84Hr0RHi_w98tHuBUJCOfverF0EnmJI2JIMB_2qfOn18doso1lcmOaM8uop_x_76kyR0xHRVxuDZA0gWYsA_kggfh_UR6B9Kx87c2DjRknDBVdgPVRIJ44cg-Snvn-NPJAWSprW3oZ62HToy9E5bWQ")',
          }}
        >
          <div className="text-center">
            <h1 className="text-white text-4xl md:text-5xl font-black font-display leading-tight tracking-[-0.03em]">
              Treasures of the Mekong: A Trilateral Manuscript Collaboration
            </h1>
            <p className="text-white mt-2 md:mt-3">
              A brief, inspiring description of the project's mission and scope, uniting
              the cultural heritage of Chiang Mai, Laos, and the Philippines.
            </p>
          </div>

          <form
            className="w-full max-w-[640px]"
            onSubmit={(e) => {
              e.preventDefault();
              onSearch?.();
            }}
          >
            <div className="flex h-14 md:h-16">
              <div className="flex items-center justify-center pl-3 pr-2 rounded-l-lg border border-r-0 border-[#dce2e5] dark:border-gray-700 bg-white dark:bg-gray-900 text-[#637c88]">
                <span className="material-symbols-outlined">search</span>
              </div>
              <input
                className="flex-1 border border-l-0 border-r-0 border-[#dce2e5] dark:border-gray-700 rounded-none bg-white dark:bg-gray-900 px-3 focus:outline-none text-sm md:text-base text-[#111518] dark:text-white placeholder:text-[#637c88]"
                placeholder="Search across all manuscripts and collections..."
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                aria-label="ค้นหา"
              />
              <button
                type="submit"
                className="px-4 md:px-5 rounded-r-lg border border-l-0 border-[#dce2e5] dark:border-gray-700 bg-accent text-white font-bold"
              >
                Search
              </button>
            </div>
          </form>
        </div>
      </div>
    </section>
  );
}

function Card({ item, onOpen }) {
  const title = titleOf(item);
  const desc = descOf(item);
  const thumb = thumbOf(item);

  return (
    <article className="flex flex-col h-full bg-white dark:bg-gray-900 rounded-xl overflow-hidden border border-gray-200 dark:border-gray-800 shadow-sm hover:shadow-lg transition-shadow duration-300">

      <div className="w-full bg-gray-100">
  <div className="relative w-full aspect-[3/4] flex items-center justify-center">
    {thumb ? (
      <img
        src={thumb}
        alt={title}
        className="w-full h-full object-contain"
        loading="lazy"
      />
    ) : (
      <div className="w-full h-full bg-gradient-to-br from-gray-200 to-gray-100" />
    )}
  </div>
</div>

      <div className="flex flex-col gap-2 p-4 grow">
  <p className="text-primary text-sm font-bold">ข้อความ</p>
  <h3 className="text-[#111518] dark:text-white text-base font-medium font-display leading-snug line-clamp-2">
    {title}
  </h3>
  <span className="text-xs text-gray-500">
    วันที่สร้าง: {createdOf(item)}
  </span>

  <div className="mt-auto pt-2">
    <button
      onClick={() => onOpen(item)}
      className="bg-primary text-white text-sm font-bold px-4 py-2 rounded-lg"
    >
      เปิดอ่าน PDF
    </button>
  </div>
</div>

    </article>
  );
}

function Footer() {
  return (
    <footer className="bg-white dark:bg-gray-900 border-t border-gray-200 dark:border-gray-800 px-4 sm:px-10 lg:px-40 py-8">
      <div className="max-w-7xl mx-auto grid grid-cols-1 md:grid-cols-4 gap-8">
        <div className="flex flex-col gap-4">
          <div className="flex items-center gap-4">
            <div className="size-6 text-primary">
              <svg className="fill-current" viewBox="0 0 24 24">
                <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-1 17.93c-3.95-.49-7-3.85-7-7.93 0-.62.08-1.21.21-1.79L9 15v1c0 1.1.9 2 2 2v1.93zm6.9-2.54c-.26-.81-1-1.39-1.9-1.39h-1v-3c0-.55-.45-1-1-1H8v-2h2c.55 0 1-.45 1-1V7h2c1.1 0 2-.9 2-2v-.41c2.93 1.19 5 4.06 5 7.41 0 2.08-.8 3.97-2.1 5.39z" />
              </svg>
            </div>
            <h3 className="text-[#111518] dark:text-white text-lg font-bold font-display">
              Treasures of the Mekong
            </h3>
          </div>
          <p className="text-[#637c88] dark:text-gray-400 text-sm">
            © 2024 Treasures of the Mekong. All rights reserved.
          </p>
        </div>

        <div className="flex flex-col gap-3">
          <h4 className="footer-title">Legal</h4>
          <a className="footer-link" href="#">Privacy Policy</a>
          <a className="footer-link" href="#">Terms of Service</a>
          <a className="footer-link" href="#">Data Usage Policy</a>
        </div>

        <div className="flex flex-col gap-3">
          <h4 className="footer-title">Get in Touch</h4>
          <a className="footer-link" href="#">Contact Us</a>
          <a className="footer-link" href="#">Report an Issue</a>
        </div>

        <div className="flex flex-col gap-3">
          <h4 className="footer-title">Developers</h4>
          <a className="footer-link flex items-center gap-2" href="#">
            <span>API Status</span>
            <span className="w-2.5 h-2.5 bg-green-500 rounded-full" />
          </a>
          <a className="footer-link" href="#">Documentation</a>
        </div>
      </div>
    </footer>
  );
}

/** ====== App ====== */
export default function App() {
  const [items, setItems] = useState([]);
  const [query, setQuery] = useState("");
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState("");

  useEffect(() => {
    load();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const load = async () => {
    try {
      setLoading(true);
      if (
        !process.env.REACT_APP_API_KEY_IDENTITY ||
        !process.env.REACT_APP_API_KEY_CREDENTIAL
      ) {
        throw new Error("API keys not found. Please check your .env file.");
      }
      // ถ้าจะใช้ fulltext จริง ๆ ให้เปลี่ยนเป็น api(`/items?fulltext_search=${encodeURIComponent(query)}`)
      const data = await fetchJsonWithProxies(api("/items"));
      setItems(Array.isArray(data) ? data : []);
      setErr("");
    } catch (e) {
      setErr(e?.message || "โหลดข้อมูลไม่สำเร็จ");
    } finally {
      setLoading(false);
    }
  };

  const filtered = useMemo(() => {
    if (!query.trim()) return items;
    const q = query.toLowerCase();
    return items.filter((it) => {
      const t = titleOf(it).toLowerCase();
      const d = descOf(it).toLowerCase();
      return t.includes(q) || d.includes(q);
    });
  }, [items, query]);

  const openPDF = async (item) => {
    try {
      const mediaId = item?.["o:primary_media"]?.["o:id"];
      if (!mediaId) throw new Error("ไม่พบไฟล์หลักของรายการนี้");
      const media = await fetchJsonWithProxies(api(`/media/${mediaId}`));
      const url = media?.["o:original_url"];
      if (!url) throw new Error("ไม่พบ URL ของไฟล์ PDF");
      window.open(url, "_blank");
    } catch (e) {
      alert(e?.message || "เปิดไฟล์ไม่สำเร็จ");
    }
  };

  return (
    <div className="min-h-screen bg-background-light dark:bg-background-dark text-[#111518] dark:text-white">
      <SiteHeader query={query} setQuery={setQuery} onSearch={() => {}} />
      <main className="flex-1">
        <Hero query={query} setQuery={setQuery} onSearch={() => {}} />

        <section className="px-4 sm:px-10 lg:px-40">
          <h2 className="section-title">Featured Manuscripts</h2>

          {loading ? (
            <div className="state">กำลังโหลด…</div>
          ) : err ? (
            <div className="state text-red-500">เกิดข้อผิดพลาด: {err}</div>
          ) : filtered.length === 0 ? (
            <div className="state">ไม่พบรายการ</div>
          ) : (
            <div className="grid grid-cols-[repeat(auto-fit,minmax(250px,1fr))] items-stretch gap-6 p-4">
              {filtered.slice(0, 12).map((item) => (
                <Card key={item["o:id"]} item={item} onOpen={openPDF} />
              ))}
            </div>
          )}
        </section>

        {/* หมายเหตุ: Sections "Our Collections" / "Recent Articles" สามารถเชื่อม Omeka S ได้ในขั้นต่อไป */}
      </main>
      <Footer />
    </div>
  );
}
