import React, { useEffect, useState, useRef } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { Document, Page, pdfjs } from "react-pdf";
import "react-pdf/dist/Page/AnnotationLayer.css";
import "react-pdf/dist/Page/TextLayer.css";
import SiteHeader from "../components/SiteHeader";
import Footer from "../components/Footer";

// ✅ ใช้ worker จาก CDN (เหมาะกับ CRA)
pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.min.js`;

export default function PdfReaderPage() {
  const { id } = useParams(); // รับ id จาก URL เช่น /read/:id
  const navigate = useNavigate();
  const [pdfUrl, setPdfUrl] = useState("");
  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(1);
  const [loading, setLoading] = useState(true);
  const [width, setWidth] = useState(window.innerWidth);
  const topRef = useRef(null);

  /** ============ โหลด URL PDF จาก Omeka S ============ */
  useEffect(() => {
    const fetchPdf = async () => {
      try {
        setLoading(true);
        const RAW_BASE = process.env.REACT_APP_API_BASE_URL || "";
        const BASE_URL = RAW_BASE.replace(/\/+$/, "");
        const withKeys = (url) => {
          const key_identity = process.env.REACT_APP_API_KEY_IDENTITY;
          const key_credential = process.env.REACT_APP_API_KEY_CREDENTIAL;
          const sep = url.includes("?") ? "&" : "?";
          return `${url}${sep}key_identity=${key_identity}&key_credential=${key_credential}`;
        };
        const api = (path) =>
          withKeys(`${BASE_URL}${path.startsWith("/") ? path : `/${path}`}`);

        const res = await fetch(api(`/media/${id}`));
        const data = await res.json();
        const url = data?.["o:original_url"];
        if (!url) throw new Error("ไม่พบไฟล์ PDF");
        setPdfUrl(url);
      } catch (e) {
        console.error(e);
        alert("ไม่สามารถโหลดไฟล์ PDF ได้");
      } finally {
        setLoading(false);
      }
    };
    fetchPdf();
  }, [id]);

  /** ============ Responsive width ============ */
  useEffect(() => {
    const handleResize = () => setWidth(window.innerWidth);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  /** ============ Scroll to Top ============ */
  const scrollToTop = () => {
    if (topRef.current) topRef.current.scrollIntoView({ behavior: "smooth" });
  };

  /** ============ Render ============ */
  return (
    <div className="min-h-screen bg-[#fbf6ed] text-[#5b4a3e]" ref={topRef}>
      <SiteHeader />
      <div className="h-[88px]" />

      <main className="max-w-6xl mx-auto px-4 sm:px-6 py-6">
        {/* ปุ่มกลับ */}
        <div className="flex justify-between items-center mb-4">
          <button
            onClick={() => navigate("/books")}
            className="px-4 py-2 bg-[#d8653b] text-white rounded hover:bg-[#c85a32]"
          >
            ← กลับไปหน้ารวมหนังสือ
          </button>
          <button
            onClick={scrollToTop}
            className="px-3 py-2 text-[#5b4a3e] hover:text-[#d8653b]"
          >
            ↑ กลับขึ้นด้านบน
          </button>
        </div>

        {/* ส่วนอ่าน PDF */}
        {loading ? (
          <div className="flex flex-col items-center justify-center py-20">
            <div className="animate-spin rounded-full h-10 w-10 border-b-2 border-[#d8653b]" />
            <p className="mt-3 text-[#a36a46] font-medium">
              กำลังโหลดหนังสือ...
            </p>
          </div>
        ) : (
          <div className="flex flex-col items-center">
            {pdfUrl ? (
              <Document
                file={pdfUrl}
                onLoadSuccess={({ numPages }) => setNumPages(numPages)}
                loading="กำลังโหลดเอกสาร..."
                className="shadow-md bg-[#fffdf6] rounded-lg"
              >
                {/* อ่านแบบหน้าคู่เมื่อจอใหญ่ */}
                {Array.from(
                  new Array(numPages),
                  (_, i) =>
                    width > 1024 ? (
                      // --- โหมดหน้าคู่ (Desktop)
                      i % 2 === 0 && (
                        <div key={i} className="flex justify-center mb-4 gap-4">
                          <Page
                            pageNumber={i + 1}
                            width={width / 2.5}
                            renderAnnotationLayer={false}
                            renderTextLayer
                          />
                          {i + 2 <= numPages && (
                            <Page
                              pageNumber={i + 2}
                              width={width / 2.5}
                              renderAnnotationLayer={false}
                              renderTextLayer
                            />
                          )}
                        </div>
                      )
                    ) : (
                      // --- โหมดหน้าเดี่ยว (มือถือ / แท็บเล็ต)
                      <div key={i} className="flex justify-center mb-4">
                        <Page
                          pageNumber={i + 1}
                          width={width - 40}
                          renderAnnotationLayer={false}
                          renderTextLayer
                        />
                      </div>
                    )
                )}
              </Document>
            ) : (
              <p className="text-center text-red-600 mt-10">
                ไม่พบไฟล์ PDF สำหรับหนังสือนี้
              </p>
            )}
          </div>
        )}
      </main>

      <Footer />
    </div>
  );
}
