// src/pages/BookArticlesPage.jsx
import React, { useEffect, useMemo, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import SiteHeader from "../components/SiteHeader";
import { fetchItemsLite, titleOf, descOf } from "../lib/omekaClient";

/* helpers */
const toHttps = (u) =>
  u ? (u.startsWith("http://") ? u.replace(/^http:/, "https:") : u) : "";
const stripTags = (html = "") => {
  const tmp = document.createElement("div");
  tmp.innerHTML = html || "";
  return (tmp.textContent || tmp.innerText || "").trim();
};

/* คู่สีพื้นหลัง/ตัวอักษร */
const BG_PALETTE = [
  { bg: "#ff8a3d", text: "#ffffff" },
  { bg: "#f6d4b1", text: "#5b4a3e" },
  { bg: "#f0c2a8", text: "#5b4a3e" },
  { bg: "#ffd7a0", text: "#5b4a3e" },
  { bg: "#ffe9d6", text: "#5b4a3e" },
];

/* แปลงค่า span เป็น “คลาสคงที่” ของ Tailwind (ห้ามใช้ template literal) */
function spanClasses(cols, rows) {
  const colMap = {
    2: "md:col-span-2",
    3: "md:col-span-3",
    4: "md:col-span-4",
    6: "md:col-span-6",
  };
  const rowMap = {
    2: "md:row-span-2",
    3: "md:row-span-3",
  };
  const col = colMap[cols] || "md:col-span-2";
  const row = rowMap[rows] || "md:row-span-2";
  // mobile ให้เริ่ม 2x2 เพื่อสวยและอ่านง่าย
  return `col-span-2 row-span-2 ${col} ${row}`;
}

/* ================ PAGE ================ */
export default function BookArticlesPage() {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    (async () => {
      try {
        const res = await fetchItemsLite();
        const filtered = (res || []).filter(
          (it) =>
            Array.isArray(it["dcterms:description"]) &&
            it["dcterms:description"].length > 0
        );
        setBooks(filtered);
      } catch (e) {
        console.error("โหลดรายการล้มเหลว", e);
      } finally {
        setLoading(false);
      }
    })();
  }, []);

  return (
    <div className="min-h-screen bg-[#faf7f2] text-[#111518]">
      <SiteHeader />
      {loading ? (
        <div className="h-[calc(100dvh-80px)] grid place-items-center text-[#7b6c61]">
          กำลังโหลด…
        </div>
      ) : (
        <BentoShowcase items={books} />
      )}
    </div>
  );
}

/* ================ BENTO SHOWCASE ================ */
function BentoShowcase({ items = [] }) {
  const navigate = useNavigate();

  // pattern ขนาดการ์ดให้ดูเป็น bento
  const pickSpan = (i) => {
    const k = i % 6;
    if (k === 0) return { cols: 6, rows: 3, variant: "feature" }; // ใหญ่เด่น
    if (k === 1 || k === 2) return { cols: 3, rows: 3, variant: "medium" }; // กลาง 2 ใบ
    if (k === 3) return { cols: 2, rows: 2, variant: "small" };
    if (k === 4) return { cols: 4, rows: 2, variant: "wide" };
    return { cols: 2, rows: 3, variant: "tall" };
  };

  const cards = useMemo(
    () =>
      items.map((book, i) => {
        const id = book["o:id"];
        const title = titleOf(book) || `Item #${id}`;
        const clean = stripTags(descOf(book) || "");
        const desc = clean.length > 160 ? clean.slice(0, 160) + "…" : clean || " ";
        const rawThumb =
          book?.thumbnail_display_urls?.large ||
          book?.thumbnail_display_urls?.medium ||
          book?.["o:thumbnail_urls"]?.large ||
          book?.["o:thumbnail_urls"]?.medium ||
          "";
        const cover = toHttps(rawThumb) || "/assets/placeholder.webp";

        const { cols, rows, variant } = pickSpan(i);
        const { bg, text } = BG_PALETTE[i % BG_PALETTE.length];
        const isLightText = text.toLowerCase() === "#ffffff";

        return (
          <article
            key={id}
            className={`${spanClasses(cols, rows)} group relative overflow-hidden rounded-3xl ring-1 ring-[#eadfce] shadow-sm hover:shadow-lg transition`}
            style={{ background: bg, color: text }}
          >
            {/* เนื้อหาในการ์ด */}
            <div className="h-full w-full grid">
              <div
                className={`relative p-4 sm:p-6 md:p-7 lg:p-8 ${
                  variant === "feature"
                    ? "grid grid-cols-2 gap-6 md:gap-8 items-center"
                    : ""
                }`}
              >
                {/* ซ้าย: ข้อความ */}
                <div className={variant === "feature" ? "order-1" : "order-1 z-10"}>
                  <h3
                    className={`font-extrabold leading-tight ${
                      variant === "feature"
                        ? "text-2xl md:text-4xl lg:text-5xl"
                        : variant === "medium"
                        ? "text-xl md:text-2xl"
                        : "text-lg md:text-xl"
                    }`}
                    style={{
                      textShadow: isLightText ? "0 1px 0 rgba(0,0,0,.08)" : "none",
                    }}
                  >
                    {title}
                  </h3>

                  {(variant === "feature" || variant === "medium") && (
                    <p
                      className="mt-3 opacity-90 max-w-prose text-sm md:text-base"
                      style={{
                        textShadow: isLightText ? "0 1px 0 rgba(0,0,0,.06)" : "none",
                      }}
                    >
                      {desc}
                    </p>
                  )}

                  <div className="mt-4 flex flex-wrap gap-2">
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        navigate(`/book/${id}`);
                      }}
                      className={`inline-flex items-center h-10 px-4 rounded-full font-semibold transition shadow ${
                        isLightText
                          ? "bg-white text-[#5b4a3e] hover:opacity-95"
                          : "bg-[#5b4a3e] text-white hover:opacity-95"
                      }`}
                    >
                      Discover <span className="ml-2">→</span>
                    </button>
                  </div>
                </div>

                {/* ขวา: รูปปก */}
                <div className={variant === "feature" ? "order-2" : "order-2"}>
                  <div
                    className={`pointer-events-none select-none ${
                      variant === "feature"
                        ? "mx-auto w-[85%] aspect-[3/4]"
                        : variant === "medium"
                        ? "absolute right-3 bottom-2 w-[50%] aspect-[3/4]"
                        : variant === "tall"
                        ? "absolute right-3 bottom-2 w-[55%] aspect-[3/4]"
                        : variant === "wide"
                        ? "absolute right-3 bottom-2 w-[45%] aspect-[3/4]"
                        : "absolute right-3 bottom-2 w-[50%] aspect-[3/4]"
                    }`}
                  >
                    <img
                      src={cover}
                      alt={title}
                      className="absolute inset-0 w-full h-full object-contain drop-shadow-2xl"
                      loading="lazy"
                      decoding="async"
                    />
                    <div className="absolute -bottom-3 inset-x-6 mx-auto h-8 rounded-full bg-black/20 blur-lg opacity-30" />
                  </div>
                </div>
              </div>
            </div>

            {/* ลิงก์คลุมการ์ด (ให้ทั้งการ์ดกดได้) */}
            <Link to={`/book/${id}`} className="absolute inset-0" aria-label={title} />
          </article>
        );
      }),
    [items]
  );

  if (items.length === 0) {
    return (
      <main className="pt-28 pb-16">
        <p className="text-center text-[#7b6c61]">ยังไม่มีรายการสำหรับแสดง</p>
      </main>
    );
  }

  return (
    <main className="pt-28 pb-16">
      <header className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 mb-6 md:mb-8">
        <div className="flex items-end justify-between gap-4">
          <div>
            <p className="text-xs tracking-widest uppercase text-[#7b6c61]">
              Curated
            </p>
            <h1 className="text-2xl md:text-4xl font-extrabold text-[#5b4a3e]">
              Rare Books — Bento Showcase
            </h1>
          </div>
          <Link
            to="/books"
            className="hidden sm:inline-flex items-center h-10 px-4 rounded-full bg-white ring-1 ring-[#e7d8c9] text-[#5b4a3e] hover:bg-[#fff7ee] transition shadow-sm"
          >
            ดูทั้งหมด
          </Link>
        </div>
      </header>

      {/* กริด Bento */}
      <section
        className="
          max-w-7xl mx-auto px-4 sm:px-6 lg:px-8
          grid grid-cols-2 md:grid-cols-6 gap-3 md:gap-4 lg:gap-5
          auto-rows-[10rem] md:auto-rows-[12rem] lg:auto-rows-[14rem]
        "
      >
        {cards}
      </section>
    </main>
  );
}
