import React, { useEffect, useState, useRef } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { Document, Page, pdfjs } from "react-pdf";
import "react-pdf/dist/Page/AnnotationLayer.css";
import "react-pdf/dist/Page/TextLayer.css";
import axios from "axios";
import SiteHeader from "../components/SiteHeader";
import Footer from "../components/Footer";
import BackToTop from "../components/BackToTop";

// ✅ ตั้งค่า worker ของ pdf.js (สำคัญมาก — ป้องกัน error "Invalid factory url")
// ✅ ใช้ worker จาก pdfjs-dist โดยแปลงเป็น blob (เสถียรสุด)
import workerSrc from "pdfjs-dist/build/pdf.worker.min.mjs?worker&url";

// ✅ ตั้งค่า workerSrc ให้ React-PDF ใช้งานได้ทั้ง dev และ deploy
pdfjs.GlobalWorkerOptions.workerSrc = workerSrc;

// ===== Helper สำหรับเชื่อม Omeka S =====
const RAW_BASE = process.env.REACT_APP_API_BASE_URL || "";
const BASE_URL = RAW_BASE.replace(/\/+$/, "");
const withKeys = (url) => {
  const key_identity = process.env.REACT_APP_API_KEY_IDENTITY;
  const key_credential = process.env.REACT_APP_API_KEY_CREDENTIAL;
  const sep = url.includes("?") ? "&" : "?";
  return `${url}${sep}key_identity=${key_identity}&key_credential=${key_credential}`;
};
const api = (path) =>
  withKeys(`${BASE_URL}${path.startsWith("/") ? path : `/${path}`}`);

const fetchJsonWithProxies = async (finalUrl) => {
  try {
    const viaAllorigins = `https://api.allorigins.win/get?url=${encodeURIComponent(finalUrl)}`;
    const r1 = await axios.get(viaAllorigins, { timeout: 15000 });
    return JSON.parse(r1.data.contents);
  } catch {}
  try {
    const viaCodetabs = `https://api.codetabs.com/v1/proxy?quest=${encodeURIComponent(finalUrl)}`;
    const r2 = await axios.get(viaCodetabs, { timeout: 15000 });
    return r2.data;
  } catch {}
  const r3 = await axios.get(finalUrl, { timeout: 15000 });
  return r3.data;
};

// ======= Component =======
export default function PdfReaderPage() {
  const { mediaId } = useParams();
  const navigate = useNavigate();

  const [pdfUrl, setPdfUrl] = useState(null);
  const [loading, setLoading] = useState(true);
  const [numPages, setNumPages] = useState(null);
  const [width, setWidth] = useState(window.innerWidth);
  const [error, setError] = useState("");

  const viewerRef = useRef(null);

  // ติดตามการเปลี่ยนขนาดหน้าจอ (responsive)
  useEffect(() => {
    const handleResize = () => setWidth(window.innerWidth);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // โหลด PDF จาก Omeka S
  useEffect(() => {
    const load = async () => {
      try {
        setLoading(true);
        const media = await fetchJsonWithProxies(api(`/media/${mediaId}`));
        const url = media?.["o:original_url"];
        if (!url) throw new Error("ไม่พบไฟล์ PDF ในระบบ");
        setPdfUrl(url);
        setError("");
      } catch (e) {
        setError(e?.message || "โหลดไฟล์ไม่สำเร็จ");
      } finally {
        setLoading(false);
      }
    };
    load();
  }, [mediaId]);

  // เมื่อโหลดเสร็จ
  const onDocumentLoadSuccess = ({ numPages }) => {
    setNumPages(numPages);
  };

  // scroll ไปบนสุดเมื่อเปลี่ยนหน้า
  const scrollToTop = () => {
    if (viewerRef.current) viewerRef.current.scrollTo({ top: 0, behavior: "smooth" });
  };

  return (
    <div className="min-h-screen flex flex-col bg-[#f8f5f0] text-[#5b4a3e]">
      {/* Header */}
      <SiteHeader />
      <div className="h-[88px]" />

      {/* ส่วนหัวของหน้าอ่าน */}
      <div className="bg-[#5b4a3e] text-white text-center py-6 shadow-md">
        <h1 className="text-xl md:text-2xl font-semibold">กำลังอ่านหนังสือ</h1>
        <button
          onClick={() => navigate("/books")}
          className="mt-3 px-4 py-2 bg-[#d8653b] text-white rounded-lg font-medium hover:bg-[#c7542b] transition"
        >
          ← กลับไปหน้ารวมหนังสือ
        </button>
      </div>

      {/* เนื้อหาหลัก */}
      <div
        ref={viewerRef}
        className="flex-1 overflow-y-auto px-2 sm:px-6 md:px-12 py-6 flex flex-col items-center"
      >
        {/* Loading State */}
        {loading && (
          <div className="flex flex-col items-center justify-center h-[60vh] text-[#5b4a3e]">
            <div className="animate-spin rounded-full h-12 w-12 border-t-4 border-[#d8653b] mb-4" />
            <p className="text-lg font-medium">กำลังโหลดหนังสือ...</p>
          </div>
        )}

        {/* Error */}
        {error && !loading && (
          <div className="text-center text-red-600 py-10">{error}</div>
        )}

        {/* ตัวอ่าน PDF */}
        {pdfUrl && !loading && (
          <div className="w-full flex flex-col items-center">
            <Document
              file={pdfUrl}
              onLoadSuccess={onDocumentLoadSuccess}
              loading={
                <div className="text-center text-[#5b4a3e] mt-10">กำลังโหลดหน้า...</div>
              }
            >
              {/* Responsive: ถ้าหน้าจอใหญ่ → อ่านหน้าคู่, ถ้าหน้าจอเล็ก → หน้าเดี่ยว */}
              {Array.from(new Array(numPages), (_, index) => {
                const pageNumber = index + 1;

                if (width > 1024 && pageNumber % 2 === 1 && pageNumber < numPages) {
                  // อ่านหน้าคู่ (หน้าปกติ + หน้าถัดไป)
                  return (
                    <div
                      key={pageNumber}
                      className="flex justify-center gap-4 mb-4 bg-white shadow-md p-2 rounded-lg"
                    >
                      <Page
                        pageNumber={pageNumber}
                        width={500}
                        renderTextLayer={true}
                        renderAnnotationLayer={false}
                      />
                      <Page
                        pageNumber={pageNumber + 1}
                        width={500}
                        renderTextLayer={true}
                        renderAnnotationLayer={false}
                      />
                    </div>
                  );
                }

                // อ่านหน้าเดียว (มือถือ / หน้าสุดท้าย)
                if (width <= 1024 || pageNumber % 2 === 0) {
                  return (
                    <div
                      key={pageNumber}
                      className="flex justify-center mb-4 bg-white shadow-md p-2 rounded-lg"
                    >
                      <Page
                        pageNumber={pageNumber}
                        width={Math.min(width - 40, 600)}
                        renderTextLayer={true}
                        renderAnnotationLayer={false}
                      />
                    </div>
                  );
                }

                return null;
              })}
            </Document>
          </div>
        )}
      </div>

      <BackToTop />
      <Footer />
    </div>
  );
}
